INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6982, 'Спортивный центр с универсальным игровым залом и плавательным бассейном', 'Sports and fitness complex with a universal gym and a swimming pool', true, 'Спортивный центр с универсальным игровым залом, бассейн. Спортивный центр будет расположен в центре Краснокаменска. Уже сейчас там идут подготовительные к строительству работы, огораживается площадь. У жителей Краснокаменска появится возможность заниматься спортом и интересно проводить своё время в новом, универсальном, построенном по современным технологиям спортивном центре.', 'Строительство ведётся с использованием типовых технологий.', NULL, NULL, 'Город Краснокаменск и Краснокаменский район', 'Забайкальский край', NULL, 'г. Краснокаменск', 'Krasnokamensk', 'Забайкальский край, г. Краснокаменск', NULL, 76621101, NULL, 'строительство', '2014-02-03', '2015-12-30', 160500000, NULL, NULL, NULL, NULL, 'aleksey.karelin1985@mail.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, волейбол, плавание', 118.0323, 50.0929, 4);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6985, 'Спортивно-оздоровительный комплекс', 'Sports and fitness complex', true, NULL, 'На территории муниципального образования до ввода объекта в эксплуатацию отсутствовали спортивные объекты соответствующие современным стандартам. В настоящее время с вводом объекта в эксплуатацию:  -повысился уровень обеспеченности спортивными объектами -появилась возможность вести учебно-тренировочный процесс спортсменов и проводить занятия по начальной полготовки - у всех групп населения появилась возможность заниматься физической культурой и спортом, что в свою очередь позволило снизить уровень заболеваемости населения. Общая площадь здания – 3490 м2,  полезная площадь – 3260 м2,  строительный объём здания – 23290 м3, в том числе ниже отметки 0.000 – 2560 м3, площадь участка – 1,97 га,  площадь застройки – 0,24 га, площадь озеленения – 1,11 га,  площадь подъездов, тротуаров, площадок – 0,62 га. В состав проекта «Строительство спортивно - оздоровительного комплекса г. Слюдянка» входит: игровой зал 18х55 м., зал тяжелой атлетики, тир, бильярдная, гимнастический зал, зал для бокса, зал для борьбы, тренажерный зал, зал для настольного тенниса, медицинский пункт, кафе – столовая, открытые спортивные площадки для игры в футбол, баскетбол, волейбол, городки, теннис и для занятий легкой атлетикой. ', NULL, 'On the territory of the municipality before commissioning there were noп sports facilities that meet modern standards.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt Currently, with the introduction of object in operation:&lt br&gt &lt br&gt - Increased the level of provision of sports facilities&lt br&gt &lt br&gt &lt br&gt - There is an opportunity to conduct the training process of athletes and conduct classes in elementary proper training&lt br&gt &lt br&gt &lt br&gt - All groups have the opportunity to be engaged in physical culture and sport, which in turn has reduced the incidence of the population.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt Total area of the building - 3490 m2,&lt br&gt &lt br&gt Useful area - 3260 m2,&lt br&gt &lt br&gt Construction of the building volume - 23290 m3, including below 0.000 - 2560 m3, the area - 1.97 ha,&lt br&gt &lt br&gt &lt br&gt building area - 0.24 ha,&lt br&gt &lt br&gt &lt br&gt landscaping area - 1.11 ha,&lt br&gt &lt br&gt area of entrances, walkways, platforms - 0.62 ha.&lt br&gt &lt br&gt The structure of the project &quot Construction of sports - recreation complex Slyudyanka&quot  includes: game room 18x55 m., Weightlifting gym, shooting, billiards room, gym, gym for boxing, wrestling room, fitness room, table tennis room, medical center , cafe - dining room, outdoor sports fields for soccer, basketball, volleyball, skittles, tennis and athletics.', 'Слюдянский район', 'Иркутская область', NULL, 'г. Слюдянка', 'Sludyanka', 'Иркутская область, г. Слюдянка, ул. Ленина, 1/а', NULL, 25634101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-04-01', '2014-05-28', 51000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, бильярдный спорт, бокс, волейбол, вольная борьба, лёгкая атлетика, настольный теннис, спортивная гимнастика, тяжёлая атлетика, футбол', 103.6902, 51.6673, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6986, 'Комплекс объектов  для СКГУ «Академия зимних видов спорта»', 'Complex of facilities for «Academy of winter sports»', true, 'Строительство горнолыжных трасс необходимо для повышения уровня физической подготовки спортсменов по слалому, сноуборду, горнолыжному спорту, а также для обеспечения оптимальной работы между спортивными объектами,  предполагаемых к строительству в дальнейшем в рамках строительства объектов для Академии зимних видов. Горнолыжная трасса для тренировок по слалому с системой искусственного снегообразования, подвесной канатной буксировочной дорогой, судейскими домиками, кафе на вершине склона Николаевской сопки будет располагаться в непосредственной близости от строящегося спортивно-тренировочного блока «Горный». Ввод в эксплуатацию спортивно-тренировочного блока «Горный» и горнолыжной трассы предоставит возможность для создания единого комплекса для повышения уровня физической подготовки спортсменов по слалому, сноуборду, горнолыжному спорту и для их эксплуатации СКГАУ «Академия зимних видов спорта», КГБОУ ДОД «СДЮСШОР по горнолыжному спорту», федерацией горнолыжного спорта, сноуборда, а также для обеспечения оптимальной работы между спортивными объектами, предполагаемых к строительству в дальнейшем в рамках строительства объектов для Академии зимних видов.  Строительство данных объектов позволит создать материальную базу для подготовки спортсменов на всех этапах, включая учебно-тренировочные сборы, проводить соревнования международного, российского, регионального уровня. Ввод горнолыжной трассы в эксплуатацию позволит увеличить показатель обеспеченности населения Красноярского края на 0,04&#37 , единовременной пропускной способности объекта на 20 человек, при этом количество систематически занимающихся физкультурой и спортом по краю увеличится на 233человек. ', 'При строительстве использовались типовые технологические решения.', 'Construction of ski trails is necessary to increase the level of physical preparation of athletes in slalom, snowboarding, alpine skiing, as well as to ensure optimum operation between sports facilities proposed construction site in the future for the construction of facilities for the Academy of winter sports.&lt br&gt Ski training for slalom with a system of artificial snow, rope tow road, referees'' houses, a cafe on the top of the slope of a hill Mykolaiv will be located in the vicinity of the building under a sports-training unit &quot Mountain&quot . Putting into operation of sports-training unit &quot Mountain&quot  and the ski slopes will provide an opportunity to create a single set to increase physical preparation of athletes in slalom, snowboard, ski and for their operation by &quot Academy of winter sports&quot , Youth Sports School &quot ski Federation&quot , snowboarding, as well as to ensure optimum operation between sports facilities of the proposed construction site in the future for the construction of facilities for the Academy of winter.&lt br&gt &lt br&gt &lt br&gt Construction of these facilities will create the material basis for the preparation of athletes at all stages, including training camps, competitionsяof international, Russian, regional level.&lt br&gt Puttingя the ski slopes in operation will increase the rate of availability of the Krasnoyarsk Territory by 0.04%, capacity of theя facility up to 20 people, with the number of regularly engaged in sports in the region will increase by 233 people.', 'During the construction there were used model technological solutions.', 'г.о. город Красноярск', 'Красноярский край', NULL, 'г. Красноярск', 'Krasnoyarsk', 'Красноярский край, г. Красноярск, Николаевская сопка', NULL, 4701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2014-01-13', '2015-12-30', 431360000, NULL, NULL, NULL, NULL, 'tutishkina@stmpkrsk.ru', 'комплекс горнолыжный', '(391) 211-44-63', NULL, 'горнолыжный спорт, сноуборд', 92.7357, 56.0014, 2);
