INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (8076, 'ФГОУ ВПО «Сибирский государственный университет ФКиС»', 'FSBEI HPO «Siberian Academy of Physical Culture» Omsk', true, 'Комплекс спортивных сооружений предназначен для проведения учебно-тренировочных занятий среди студентов университета физической культуры, а также проведения соревнований.  Сроки строительства объекта – 2006-2009 годы Стоимость строительства - 161,07 млн. рублей  Строительство объекта включает в себя: •    Пристройку теннисного корта к бассейну &quot Альбатрос&quot . Работы по строительству объекта завершены в 2006 году. Пропускная способность – 12 чел/смена. •    Реконструкцию плавательного бассейна &quot Альбатрос&quot . Работы по реконструкции объекта завершены в 2009 году. Пропускная способность – 120 чел/смена.', NULL, 'Complex of sports facilities designed for training sessions with students of the University of Physical Education, as well as competitions  Construction of the project - 2006-2009, The cost of construction - 161 070 000 rubles  Construction of the facility includes: • Enclosed tennis court to the pool &quot Albatross&quot  Work on the construction of the facility was completed in 2006 Capacity - 12 people / shift • Reconstruction of the swimming pool &quot Albatross&quot  Work on the reconstruction of the facility was completed in 2009 Capacity - 120 persons / shift ', NULL, 'Город Омск', 'Омская область', 'федерально-значимый', 'г. Омск', 'Omsk', 'Омская область, г. Омск', NULL, 52701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, NULL, 161070700, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, NULL, 73.3682, 54.9893, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (8519, 'Универсальный зал', 'Universal gym', true, 'На Универсальном зале планируется постоянное расположение: ГБУ ДОД «СДЮСШ по хоккею с шайбой РМ»  НП «Хоккейный клуб «Мордовия»  Специализированная детско-юношеская спортивная школа олимпийского резерва по фигурному катанию на коньках Республики Мордовия  ГБУ РМ ДОД «Специализированная детско-юношеская спортивная школа по баскетболу и волейболу»  НП «Баскетбольный клуб «Рускон-Мордовия»  ГБУ РМ ДОД «Академия И. Винер – Специализированная детско-юношеская спортивная школа олимпийского резерва по художественной гимнастике»  АУ ДОД «Теннисный центр Мордовии»  ГБУ ДО «Школа высшего спортивного мастерства РМ»  ГБУ РМ ДОД «Специализированная детско-юношеская спортивная школа олимпийского резерва по боксу имени Олега Маскаева»  ГБУ РМ ДОД «СДЮСШОР по борьбе им. А.В. Мишина»  ГБУ РМ ДОД «Специализированная детско-юношеская спортивная школа олимпийского резерва по гимнастике Л.Я. Аркаева». На Универсальном зале планируется провести Финал Гран-при по фигурному катанию 2015/2016гг.  ', 'Общая площадь здания – 53 998,54 м2. По вертикале объект разделен на семь уровней &#40 семь этажей&#41 . На 2-х нижних уровнях &#40 на цокольных этажах&#41  расположатся семейный спортивно-развлекательный центр и торговые павильон на 1100 м2  медико-оздоровительный центр  зал силовых тренировок. На отметке первого уровня – ядро арены-зал трансформер, который по необходимости сможет становится ледовой ареной, площадкой для баскетбола, волейбола, минифутбола, гандбола, тенниса, концертной площадкой, местом проведения спортивных соревнований европейского и мирового уровня, также универсальный мультимедийный конференц-зал, учебный класс и пресс-центр  специализированные хоккейные раздевалки  раздевалки для других видов спорта и тренажерный зал. На объекте будут проводится конференции, форумы и массовые мероприятия. В зависимости от проводимых мероприятий количество зрительских мест может изменяться с 7,5 тыс. до 8,5 тыс. На втором уровне – ресторан и зоны фуд корта, тренировочный гимнастический &#40 хореографический&#41  зал площадью 492,78 м2, а также музей спорта, который познакомит посетителей «Универсального зала» с достижениями спортсменов Республики Мордовия и отобразит основные этапы развития спорта в регионе. На третьем уровне – административный блок, а также 7 VIP-лож и 14 клубных лож общей площадью 480 м2, помещение для фуршета и зона обеденных столиков. На четвертом уровне – административный блок. На пятом уровне – техническая зона и кабины синхронного перевода. ', 'Universal gym planns permanent location for: &quot Youth Sports School hockey PM&quot , &quot Hockey Club&quot  Mordovia &quot , Specialized Children and Youth Sports School Olympic reserve in Figure Skating Republic of Mordovia, &quot Specialized Sports school inя basketball and volleyball&quot , &quot Basketball Club&quot  Ruscon-Mordovia &quot , &quot Academy I. Wiener - Specialized Children and Youth Sports School of Olympic Reserve in rhythmic gymnastics&quot , &quot Tennis Center Mordovia&quot , &quot high school sports RM&quot , &quot Specialized Children and Youth Sports School of Olympic Reserve in boxing named afterя Oleg Maskaev&quot , SBD PM DOD &quot SDYUSSHOR to combat them. AV Mishina &quot , &quot Specialized Children and Youth Sports School of Olympic Reserve in gymnastics named after L.Y. Arkaeva. &quot &lt br&gt The universal gym is scheduled to hold Grand Prix of Figure Skating Final in 2015/2016.', 'Total area of the building - 53 998.54 m2.&lt br&gt &lt br&gt Along the vertical the facility is divided into seven levels (seven floors).&lt br&gt &lt br&gt On 2 lower levels (on ground floor) there will be a family sports andпentertainment center and shopping pavilions for 1100 m2  health and fitness center  gym for strength training.&lt br&gt &lt br&gt &lt br&gt &lt br&gt At the level of the first floor - the core of the arena -gym transformer, which by necessity will become an ice arena, basketball, volleyball, mini-football, handball, tennis, concert venue, the venue for sports events of European and world level, and versatile multimedia conference room, classroom and a a press center  specialized hockey locker rooms  locker rooms for other sports and gym. At the facility there will be conferences, forums and public events. Depending on the number ongoing activities seats can vary from 7.5 thousand. to 8.5 thousand.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt At the second level - the restaurant and food court area, fitness gym (dance) area of 492.78 m2, and the Sports Museum, which will introduce visitors of &quot Universal gym&quot  with the achievements of athletes of Republic of Mordovia and will display the main stages of the development of sport in the region.&lt br&gt &lt br&gt &lt br&gt &lt br&gt At the third level - the administrative unit, and 7 VIP-boxes and 14 club lodges with a total area of 480 m2, a room for parties and dining table area.&lt br&gt &lt br&gt At the fourth level - the administrative unit.&lt br&gt &lt br&gt At the fifth level - technical area and simultaneous interpreting booth.', 'г.о. Саранск', 'Республика Мордовия', 'регионально-значимый', 'г. Саранск', 'Saransk', 'Республика Мордовия, г. Саранск, ул. Б. Хмельницкого, 35 ', NULL, 89701000, NULL, 'строительство', NULL, '2015-12-30', 2999061500, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'лёгкая атлетика, спортивная гимнастика, тяжёлая атлетика, художественная ги