INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (9400, 'Футбольный манеж с искусственным покрытием «Дохсун»', 'Football arena with artificial turf ', true, 'Не менее важна и социальная значимость спортивного комплекса «Дохсун». Помимо проведения различных соревнований, в том числе юношеских и молодёжных, спортивный комплекс является базой подготовки сборных команд Республики Саха &#40 Якутии&#41  по нескольким видам спорта. Кроме того, в стенах «Дохсуна» будут заниматься воспитанники различных детских и юношеских школ, многие из которых вскоре станут продолжателями замечательных спортивных традиций Якутии. Спорткомплекс «Дохсун» будет местом проведения различных развлекательных концертов и других мероприятий. Но главным его значение для спортивной жизни не только Якутска, но и всей Республики Саха &#40 Якутия&#41  доступность и массовость. ', 'Единственное  футбольное поле в мире по размерам стандарта FIFA  построенное на свайном основании &#40 кол-во свай 1380 штук&#41  проветриванием подпольем. Манеж выполнен из быстро возводимых металлоконструкций канадской фирмы «XONCO», пол футбольного поля оснащен системой отопления на всю площадь футбольного покрытия, что составляет протяженность греющего трубопровода 52000 метров 52 м2. Единственный крытый футбольный манеж круглогодичного пользования, эксплуатируемый на вечно мерзлом грунте в районе Крайнего Севера. ', NULL, NULL, 'г.о. город Якутск', 'Республика Саха-Якутия', 'регионально-значимый', 'г. Якутск', 'Yakutsk', 'Республика (Саха) Якутия, г. Якутск, ул. Лермонтова 62/5', NULL, 98701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-03-15', '2012-07-05', 1375312700, NULL, NULL, NULL, NULL, 'sk_dohsun@mail.ru', 'манеж легкоатлетический', '40-25-16', NULL, 'лёгкая атлетика, настольный теннис, стендовая стрельба, стрельба из лука, фитнес-аэробика, футбол', 129.7326, 62.0281, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (9441, 'Комплекс трамплинов ГОУ СДЮШОР «Аист»', 'Jump complex SEI Secondary Youth Sports School of Olympic Reserve «Aist»', true, 'Работы по строительству и реконструкции объектов комплекса на горе Долгой производились с учетом  норм, установленных Международной  федерацией лыжного спорта &#40 FIS&#41 , летом 2013 года на трамплины комплекса получены сертификаты соответствия требованиям FIS. Соответствие техническим нормам и требованиям Международной  федерации лыжного спорта FIS обеспечивает проведение учебно-тренировочного и соревновательного процесса сборной команды России и региональных команд на высоком современном уровне.  Реконструкция комплекса позволила создать региональный центр  по прыжкам на лыжах с трамплина, обеспечивающий максимально комфортные условия для подготовки  российских спортсменов к ХХII зимним Олимпийским 2014 года в г. Сочи, а также для проведения соревнований по прыжкам на лыжах с трамплина, лыжному двоеборью, горнолыжным видам спорта регионального, всероссийского, международного уровня, а также для проведения  учебно-тренировочного процесса и предоставления физкультурно-оздоровительных услуг населению. Уже сейчас комплекс трамплинов на горе Долгая заслужил высокою оценку со стороны специалистов,  прибывающих на комплексе &#40 в т.ч. международных&#41 , которые отмечают, что он станет одним из самых современных и красивых в Мире.', 'Трамплины: Геометрические параметры трамплинов  соответствуют требованиям Международной федерации лыжного спорта, принятых в 2008 году. Трамплины К-90, К-120 сертифицированы на соответствие FIS.  Современные судейские, тренерские платформы, а также система оценки, светофор, система Swith timing, светодиодное табло обеспечат судейство любого уровня сложности и ответственности. Трибуны на 2000 мест обеспечивают проведение соревнований при большой зрительской поддержке. Кресельный подъемник Doppelmaier, лифты на трамплинах К-90 К-120 значительно увеличивают количество прыжков за тренировку, пропускную способность спортсооружений, качество проведения учебно-тренировочных занятий.  Современная система освещения позволяет проводить учебно-тренировочные занятия и соревнования в темное время суток.  Система намораживания Rehau, система заснеживания и искусственное покрытие на трамплинах позволит эксплуатировать спортсооружения  круглый год.  Лыжные трассы, а также  лыжный стадион, соответствующие   требованиям документов, регламентирующих планирование трасс и стадиона для лыжных гонок: «Руководство FIS по гомологации трасс для лыжных гонок», 2005 г., и «Правила для проведения международных соревнований &#40 ICR&#41 » позволят получить сертификат  FIS, а значит проводить соревнования по лыжному двоеборью и лыжным гонкам любого уровня, а также проводить все виды лыжных гонок: спринт, масстарт, персьют, эстафета. Лыжероллерная трасса протяженностью 2,5 км будет иметь большую пропускную способность и позволит проводить учебно-тренировочные занятия и соревнования в летний период спортсменов разного уровня подготовки. ', 'Work on the construction and reconstruction of the complex on Mount Dolgaya were made against the standards established by the International Ski Federation (FIS), in the summer of 2013 complex obtained certificates of conformity to the requirements of FIS on the trampoline. Compliance with technical standards and requirements of the International Ski Federation FIS ensures the implementation of the training and the competitive process of the national team of Russia and the regional teams at the highest modern standards. Reconstruction of the complex will create a regional center for the Ski jumping, providing favorable conditions for preparing Russian athletes for the XXII Olympic Winter of 2014 in Sochi, as well as for competitions in Ski jumping, Nordic combined, ski sports of regional, national, international level, as well as for the training process and the provision of health and fitness services to the population. Even now complex of trampolines on Mount Dolgaya earned high praise from experts arriving at the complex (including international), who note that it will be one of the most modern and beautiful in the world.', 'Springboards: Geometric parameters of springboards comply with the requirements of International Ski Federation, adopted in 2008. Springboards K-90, K-120 are certified for compliance with FIS.&lt br&gt &lt br&gt &lt br&gt Modern judges, coaching platforms, as well as evaluation system, traffic lights, system of Swith timing, LED display will provide the judging of any complexity and responsibility. Tribunes for 2000 seats provide competitions with great spectator support.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt Chairlift Doppelmaier, elevators on trampolines K-90 K-120 significantly increase the number of jumps per workout, the bandwidth of sports facilities, the quality of the training sessions.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt Modern lighting system allows for teaching and training sessions and competitions in the dark.&lt br&gt &lt br&gt &lt br&gt &lt br&gt System of freezing Rehau, snowing system and artificial turf on trampolines will allow sports facilities to operate year-round.&lt br&gt &lt br&gt &lt br&gt &lt br&gt Ski slopes and the ski stadium, the relevant to the requirements of the documents governing the planning of routes and cross-country skiing stadium: &quot Guide the FIS homologation of trails for cross-country skiing,&quot  2005 and &quot Regulations for international competitions (ICR)? will provide a certificate FIS, and therefore hold competitions in Nordic combined and cross-country skiing at all levels, as well as to carry out all kinds of ski racing: sprint masstart, Pursuit, relay race.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt Roller trail length of 2.5 km will have a greater capacity and will allow for educational and training sessions and competitions in summer for athletes of all skill levels.', 'г.о. город Нижний Тагил', 'Свердловская область', 'регионально-значимый', 'г. Нижний Тагил', 'Nizhny Tagil', 'Свердловская область, г. Нижний Тагил, восточный склон горы Долгая, кварталы 164, 165 городского лесничества ', '622000, Sverdlovsk region, Nizhny Tagil city, eastern slope of the mountain of debt, blocks 164, 165 urban forestry. ', 65751000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', NULL, '2015-12-30', 2068000000, '9.00 - 20.00, понедельник - выходной день', '9.00 - 20.00', '9.00 - 20.00', NULL, 'aist.nexcom@mail.ru', 'комплекс трамплинов для прыжков на лыжах', '8-(3435)-41-81-17,8-(3435)-41-81-21', 'http://aist-tramplin.ru/', 'горнолыжный спорт, лыжное двоеборье, лыжные гонки, прыжки на лыжах с трамплина, стендовая стрельба', 59.9813, 57.9101, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (9654, 'Стадион-площадка', 'Stadium Courts', true, 'Объект находится в центре города с населением 20 000 человек. В городе находится 4 общеобразовательных школы, 1 профтехучилище, 1 коррекционная школа. Все учебные заведения проводят занятия на данном стадионе.  В городе создана взрослая футбольная команда играющая 4 года по высшей лиги Республики. Проводятся более 60 различных общегородских соревнований. В городе имеются удобные подъездные пути по железнодорожному транспорту, автодороги. Имеются гостиницы. Развита система общепита. Географически район расположен на границе с Республикой Удмуртия, Пермский край, Кировская область, Башкирия. Даты крупных соревнований и значимых событий: Первенство и Чемпионат РТ по футболу среди команд Высшей лиги Республики. Розыгрыш Кубка Татарии по футболу. Первенство РТ на приз &quot Кожаный мяч&quot . Соревнования по лёгкой атлетике &quot Шиповка юных&quot . Соревнования &quot Весёлые старты&quot  среди школ. Легкоатлетическая эстафета Мира, посвященная годовщине Победа в ВОВ. ', 'На стадионе расположены: Футбольное поле с искусственным покрытием. Трибуны на 1200 посадочных мест. Тренажёрная площадка открытого типа на 20 занимающихся. дорожки с резиновым покрытием. ', 'The facility is situated in the center of the city, with a population of 20 000 people. The town has 4 secondary schools, 1 technical school, 1 correctional school.&lt br&gt All schools conduct classes at this stadium.&lt br&gt The city established an adult soccer team which has ben playing for 4 years at the major league of the Republic. More than 60 different citywide competitions have been held here.&lt br&gt The city has convenient access to Railways, highways. There are hotels. The system of public catering is developed. Geographically, the area lies on the border with the Republic of Udmurtia, Perm, Kirov region, Bashkiria.', 'The football field is a modern synthetic coating.&lt br&gt &lt br&gt &lt br&gt Treadmills are coated with rubber.', 'Агрызский район', 'Республика Татарстан', NULL, 'г. Агрыз', 'Argyz', 'Республика Татарстан, г. Агрыз, ул. Пушкина, 4', 'The Republic of Tatarstan, City Agryz, Pushkin Street, Building 4.', 92601101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2010-10-05', 22000000, NULL, NULL, NULL, NULL, NULL, 'стадион', '8-85551 234 20', NULL, 'лёгкая атлетика, общая физическая подготовка, футбол', 52.9987, 56.5301, 2);
