INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (13829, 'Спортивный комплекс «Олимп»', '«Olymp» sports complex', true, 'Собственником СК «ОЛИМП» с 2008 года является ООО «Юргинский машзавод». Кроме работников машзавода услугами спорткомплекса пользуются коллективы предприятий и учреждений города, учебные заведения, группы любителей волейбола, мини-футбола, настольного тенниса, учащиеся отделения «Волейбол» МБОУДОД «ДЮСШ-2». Основное назначение  - массовая работа с трудящимися завода и членами их семей. Кроме машзаводчан услугами спорткомплекса пользуются коллективы предприятий и учреждений города, учебные заведения, группы любителей волейбола, мини-футбола, настольного тенниса.   Спорткомплекс «Олимп» и прилегающие к нему плоскостные игровые площадки используется для проведения соревнований по видам спорта Спартакиады трудящихся города &#40 свыше 2,5 тыс.участников&#41 , Спартакиады работников ООО «Юрмаша» &#40 до 2 тыс. чел.&#41 .    На безвозмездной основе предоставляется время для занятий учащимся  групп волейбола МБОУДОД «ДЮСШ-2».      На платной основе предоставляется время занятий для трудовых коллективов ТД «Лента», МАУ «Коммунальщик», завода «Технониколь», «Комус Упаковка», организованных групп контингента ИП.      На льготных условиях услугами пользуются службы судебных приставов, ГОВД, ФСБ, службы исполнения наказания.     Всем занимающимся в спортивных группах инвентарь предоставляется бесплатно.  Даты крупных соревнований и значимых событий: &#9679  2006-2013 г.г.  туры Чемпионатов России высшей лиги «А» и «Б» &#40 регион Сибирь-Дальний Восток&#41  по волейболу среди женских команд &#9679  2010-2014 г.г. региональные турниры по настольному теннису  &#9679  2008-2014 г.г. региональные турниры по спортивным танцам «Солнечный зайчик»  &#9679  2014г.-  Всероссийский турнир по танцевальному спорту «Солнечный зайчик»  &#9679  2008 и 2010 г. чемпионат Сибирского федерального округа по стилевому каратэ  &#9679 2011г. – Всероссийский турнир по греко-римской борьбе  &#9679  2012г. – Всероссийский турнир по стилевому каратэ «Боевое братство». &#9679  2014 г. – первенство области среди юношей по греко-римской борьбе.', 'Строительство велось с использованием типовых технологических решений.', 'The owner of the sports complex &quot Olympia&quot  from 2008 is &quot Yurginskiy machine works.&quot , ltd.я Besides plant workers the services of the sports complex are used by collectives of enterprises and institutions of the city, schools, groups of amateurs in volleyball, mini football, table tennis, students of aяbranch &quot Volleyball&quot  ofя &quot Youth Sports School 2&quot .', 'Construction was carried out using standard technologies', 'Юргинский район', 'Кемеровская область', NULL, 'г. Юрга', 'Ugra', 'Кемеровская область, г. Юрга, ул. Краматорская, 1 ', 'Kemerovo Region, Jurga, street Kramatorskaya 1.', 32749000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2002-02-01', '2006-12-29', 37843000, '08:00-22:00', NULL, NULL, NULL, 'valentina.p@gmail.com', 'многофункциональный спортивный комплекс', '8-(384-51)-5-65-10, 8-(384-51)-5-67-67 ', NULL, 'волейбол, гиревой спорт, греко-римская борьба, дартс, каратэ, лёгкая атлетика, настольный теннис, общая физическая подготовка, пауэрлифтинг, спортивная акробатика, фитнес-аэробика, художественная гимнастика, шахматы', 84.9296, 55.7082, 119);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (13298, 'Центр здоровья и спорта зал школы № 12', 'Health and sports center of secondary school №12', true, 'Объект находится в густонаселенном микрорайоне города на базе самой крупной школы города с количеством обучающихся 948 человек. К третьему классу 95 &#37  обучающихся, не имеющих противопоказаний, умеют плавать.  Плавательный бассейн пользуется большой популярностью у жителей микрорайона и города. В вечерние часы его посещают до 140 жителей города.', 'Электронное дозирование реагентов, автоматический анализ состояния воды. Расположение в одном помещении малой &#40 для приучения к воде детей&#41  и большой ванны.', 'The property is situated in a densely populated district of the city on the basis the largest schools of the city with the number of students 948 people. By the third class 95% of students who have no contraindicationsthey know how to swim. Swimming pool is very popular among residents of the neighborhood and the the city. In the evening t 140 residents visit it.', 'Electronic dispensing reagents, automatic analysis of the water. Location in the same room of small (for habituation to water children) and a large bath.', 'Рыбинский район', 'Ярославская область', NULL, 'г. Рыбинск', 'Rybinsk', 'Ярославская область, г. Рыбинск, ул. Моторостроителей, 27', 'Yaroslavl Region, Rybinsk, street Motorostroiteley 27', 78715000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2009-01-05', '2010-08-31', 25000000, NULL, NULL, NULL, NULL, '79109672451@yandex.ru', 'бассейн', '8-910-967-24-51', NULL, 'морско