INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (13376, 'Центр развития спорта «Норд Арена»', '«Nord Arena» center of sports development', true, 'Универсальный игровой зал может быть использован для проведения тренировочных занятий, физкультурных и спортивных мероприятий по различным видам спорта. Плавательный бассейн с ванной размером 25х11 м позволяет проводить тренировочные занятия, соревнования и занятия по оздоровительному плаванию. Плавательный бассейн с ванной 10х6м позволяет осуществлять обучение плаванию детей от трех лет. Спортивный центр предназначен для оздоровительно-спортивной работы с учреждениями, организациями и объединениями спортивного направления различного уровня. Для организации и проведения мероприятий по повышению квалификации, семинаров и методического консультирования по развитию физической культуры и спорта на 3-м этаже имеется методический кабинет, оборудование которого обеспечивает фронтальный метод организации учебного процесса. ЦРС «Норд Арена» является доступным для маломобильных групп населения &#40 МГН&#41 . Обеспечен свободный заезд во входную зону спортивного центра и беспрепятственный доступ в помещения любого этажа при помощи лифта. На обходной дорожке бассейна 25х11м предусмотрено подъемное устройство для занимающихся МГН, оборудованы и другие вспомогательные помещения. Даты крупных соревнований и значимых событий: 22.01-26.01.2014г. Первенство СЗФО по мини-футболу среди юн. 97-98г.р., 29.01-31.01.2014г. Первенство СЗФО по спортивной акробатике, 21.01-22.02.2014г. Финал регионального этапа ШБЛ «КЭС-БАСКЕТ», 06.03-07.03.2014г. П