INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (14770, 'Футбольное поле с искусственным покрытием', 'Football field with artificial turf', true, 'Развитие футбола в нашем городе имеет огромное социальное значение. Около 220 детей в возрасте от 6 до 17 лет занимаются футболом в Электростальской «ДЮСШ по футболу «ВОСТОК», образованной в октябре 2002 года. Воспитанники неоднократно становились призерами Первенства Московской области, завоевывали призовые места на международных турнирах. Следует учесть, что в школе тренируется много детей из неполных семей и благодаря занятиям футболом сотни мальчишек избегают дурного влияния улицы. Применение современных технологий при строительстве футбольного поля с искусственным покрытием позволило практически круглогодично осуществлять тренировочный процесс. Даты крупных соревнований и значимых событий: 05.09.2012 – открытие футбольного поля с искусственным покрытием. ', 'Применение современных технологий при строительстве футбольного поля с искусственным покрытием позволило практически круглогодично осуществлять тренировочный процесс.', 'Development of football in our town has a great social value. About 220 children aged 6 to 17 years are engaged in the Elektrostal &quot Youth Sports School on football&quot  Vostok &quot , formed in October 2002. Pupils have repeatedly been medalist of the Moscow region, won prizes at international tournaments. Note that the school trains many children from single-parent families and thanks to hundreds of boys playing football avoid bad influences of the street.&lt br&gt &lt br&gt Application of modern technologies in the construction of the football field with artificial turf allowed practically year-round exercise training process.', 'Application of modern technologies in the construction of a football field with artificial turf allowed virtually year-round exercise training process.', 'Город Электросталь', 'Московская область', NULL, 'г. Электросталь', 'Elektrostal', 'Московская область, г. Электросталь, ул. Пушкина,  23/а', 'Moscow region, Elektrostal, Pushkin street, d. 23A on the territory MOU &quot Secondary School №11»', 46791000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-09-01', '2012-09-01', 22497865, NULL, NULL, NULL, NULL, 'elsport.kfis@mail.ru', 'футбольное поле ', '8(926)421-46-34', 'www.fc-vostok.ru', 'футбол', 38.4331, 55.7992, 121);
