INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (15286, 'СДЮСШОР', 'Specialized Children and Youth Sports School of the Olympic Reserve', true, 'У Дмитровской спортшколы появился стадион В городе Дмитрове на улице Большевистской завершилось строительство футбольного стадиона с искусственным покрытием. Торжественное открытие спортивного объекта состоялось в День города Дмитрова, 5 сентября 2015 года. На новом стадионе будут тренироваться воспитанники детско-юношеской спортивной школы олимпийского резерва. Помимо футбольного поля на базе объекта уложены три легкоатлетические дорожки и построены зрительские трибуны.  Строительство стадиона в Дмитрове проводилось в рамках Подпрограммы «Развитие футбола в Российской Федерации на 2008-2015 годы». Средства, выделенные на приобретение искусственного покрытия составили 8,42 млн рублей. Помимо этого еще 32 миллиона рублей были привлечены из внебюджетных источников.', NULL, NULL, NULL, 'Дмитровский район', 'Московская область', NULL, 'г. Дмитров', 'Dmitrov', 'Московская область, г. Дмитров, ул. Большевистская ', 'g.Dmitrov, ul.Bolshevistskaya', 46608101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2015-09-05', 8421000, NULL, NULL, NULL, NULL, NULL, 'футбольное поле ', NULL, NULL, 'футбол', 37.5357, 56.3432, 2);
