INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (15853, 'ГБОУ СПО Нижегородское училище олимпийского резерва имени В.С.Тишина', 'SEI SVE «Nizhny Novgorod school of Olympic reserve named after V.S.Tishina»', true, 'Нижегородское областное училище олимпийского резерва &#40 НОУОР&#41  является профессиональной образовательной организацией среднего профессионального образования физкультурно-спортивной направленности. В НОУОР наряду с профессиональными программами также реализуются основные и дополнительные общеобразовательные программы &#40 6-11 классы общеобразовательной школы и программы по видам спорта&#41 . Обучающиеся училища проходят подготовку по 18 видам спорта, среди которых баскетбол, лыжные гонки, плавание, прыжки на лыжах с трамплина, современное пятиборье, художественная гимнастика, шорт-трек. Спортсмены НОУОР обеспечиваются бесплатным проживанием в общежитии и бесплатным питанием в столовой училища, а также получают квалифицированную медицинскую помощь в медико-восстановительном центре, являющимся структурным подразделением училища. НОУОР обладает необходимой материальной базой &#40 спортивными объектами, среди которых современные спортивные и тренажерные залы, бассейн&#41 , которая позволяет проводить официальные спортивные мероприятия как городского, городского и регионального, так и всероссийского и международного уровней. Даты крупных соревнований и значимых событий: 2-3 марта 2013г. – Баскетбол : БК НН2 – БК ЦСК. 6-7 марта 2013г. – Баскетбол : БК НН2 – БК Химки. 2-3 апреля 2013г. – Чемпионат Нижегородской области по художественной гимнастике. 7-8 апреля 2013г. – Баскетбол : БК НН2 – БК Красные крылья. 1 февраля 2014г. – Кубок области по ОФП и гребля на эргометрах. 1-2 февраля 2014г. – Баскетбол : БК НН – БК Триумф. 15-16 февраля 2014г. – Баскетбол : БК НН – БК ЦСК2. 27 февраля и 1 марта 2014г. – Чемпионат области по ОФП и гребле на эргометрах. 20 марта 2014г. – Открытое первенство города Н.-Новгорода по современному пятиборью. 22-23 марта 2014г. – Баскетбол : БК НН2 – БК Енисей2. 28-30 мая 2014г. – Открытый чемпионат города Н.-Новгорода по плаванию. 7 июня 2014г. – Открытый летний чемпионат Нижегородской области по современному пятиборью.  ', 'В 2010 году был введен в эксплуатацию новый спортивный комплекс с современным оборудованием .Включает в себя: многофункциональный игровой зал оборудованный электронным табло и аудио- акустической системой, плавательный бассейн 25 метров на 6 дорожек оборудован современной системой озонирования воды и электронной судейской системой и зрительскими местами.', 'Nizhny Novgorod regional school of Olympic reserve is a professional educational organization of vocational education in sports and sports orientation. In this school along with professional programs are also implemented basic and advanced education programs (6-11 classes of secondary schools and programs for sports).&lt br&gt &lt br&gt College Students are trained in 18 sports, including basketball, cross country skiing, swimming, ski jumping, modern pentathlon, gymnastics, short track.&lt br&gt &lt br&gt Athletes of the school are provided with free accommodation in a hostel, and free school meals in the dining room, as well as receive quality medical care in medical rehabilitation center is a subdivision of the school.&lt br&gt &lt br&gt The school has the necessary material base (sports facilities, including modern gyms, swimming pool), which allow to holdя official sports events on municipal, urban and regional as well as national and international levels.', 'In 2010 was commissioned a new sports complex with modern facilities. Includes: multifunctional game room equipped with electronic scoreboard and audio sound system, a swimming pool of 25 meters by 6 lanes equipped with a modern system of water ozonation and electronic scoring system and seats.', 'Город Нижний Новгород', 'Нижегородская область', NULL, 'г. Нижний Новгород', 'Nizhny Novgorod', 'Нижегородская область, г. Нижний Новгород, ул. Ванеева, 110/б', 'Nizhny Novgorod region, Nizhny Novgorod city, street Vaneeva 110 B.', 22701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-01-15', '2010-12-31', 225100000, NULL, NULL, NULL, NULL, 'nikolaybykanov@gmail.com', 'многофункциональный спортивный комплекс', '8-910-138-34-45', NULL, 'баскетбол, плавание, современное пятиборье, тяжёлая атлетика, фехтование, художественная гимнастика', 44.0378, 56.2872, 2);
