INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (16519, 'Многофункциональный физкультурно-оздоровительный комплекс', 'Multipurpose sports and fitness complex', true, 'Обеспечение социально гарантированного уровня обслуживания населения объектами физической культуры и спорта, увеличение численности граждан систематически занимающихся физической культурой и спортом, в том числе, лиц с ограниченными возможностями здоровья на территории муниципального образования «Город Димитровград» Ульяновской области,  проведение соревнований и тренировочных занятий по игровым видам спорта &#40 баскетболу, волейболу, теннису и  др.&#41 , а также подготовки спортсменов массовых разрядов.  Даты крупных соревнований и значимых событий: Сентябрь 2014 года – май 2015 г.: - Открытый турнир городов Поволжья по национальной спортивной борьбе «Корэш» &#40 март 2015, ежегодно с 2008 по 2015&#41   - Открытый турнир по греко-римской борьбе памяти мастеров спорта В.Зуйкова и С.Мурзаева - XXV Турнир городов России среди юношей и молодежи по греко-римской борьбе памяти воинов-интернационалистов, посвященный 70-летию Великой Победы &#40 май, 2008-2015&#41 , - Всероссийский турнир по художественной гимнастике, посвященный Дню защиты детей &#40 май&#41 . - V Всероссийский турнир по боевому самбо памяти М.Ю.Дерябина &#40 октябрь 2014 г.&#41   - Всероссийский турнир по художественной гимнастике «Новогодняя снежинка» &#40 декабрь 2014 г.&#41 , -Открытый межрегиональный спортивный турнир среди сотрудников органов внутренних дел памяти С.А.Батанова &#40 октябрь 2014&#41 . ', 'Здание построено с применением легких металлических конструкций. Стеновое и кровельное ограждение здания выполнено из трехслойных панелей типа «сэндвич» с утеплением из минеральной ваты на основе базальтового волокна. ', NULL, 'The building was constructed with the use of light metal constructions. Wall and roof of the building enclosure is made of sandwich panels &quot sandwich&quot  with insulation made of mineral wool, based on basalt fiber.', 'г.о. Димитровград', 'Ульяновская область', NULL, 'г. Димитровград', 'Dimitrovgrad', 'Ульяновская область, г. Димитровград, ул. Курчатова, 3/б ', 'Ulyanovsk region Dimitrovgrad, Kurchatov Street 3 B &quot ', 73705000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2007-07-25', '2007-11-25', 16000000, '08:00-23:00', NULL, NULL, NULL, 'sportkomdd@yandex.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, волейбол, лёгкая атлетика, общая физическая подготовка, художественная гимнастика', 49.5462, 54.2294, 125);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (16557, 'Дворец спорта «Олимп»', 'Olymp sports palace', true, NULL, 'В состав строительства входят: здание крытого катка с искусственным льдом, открытые плоскостные сооружения &#40 стадион с трибунами&#41 , блочная котельная установка, трансформаторная подстанция.     Здание  крытого катка двухэтажное и имеет в плане прямоугольню форму с габаритными размерами в осях 81,5 &#215  40,2 м. Строительный объем здания  28787,3 куб.м, общая площадь 3596,0 кв.м.      - На первом этаже  расположены: спортивный зал с ледовым полем и трибунами на 204 места, зал для занятий тяжелой атлетикой и общефизической подготовкой, вестибюль, касса, гардероб, сушильная, помещение точки коньков, кладовая, тренерская, раздевальные, помещение охраны, медицинский кабинет, комната судей, инвентарная, помещение льдоуборочного комбайна, бытовые, технические  и подсобные помещения.     - На втором этаже расположены: методический кабинет, хореографический класс, кафетерий, радиорубка, административные, бытовые, подсобные помещения.     В зоне плоскостных сооружений размещены спортивный стадион, предназначенный для тренировок и соревнований. Комплекс спортивных сооружений на стадионе включает в себя следующие объекты: футбольная площадка с искусственным покрытием, две круговые и четыре линейные беговые дорожки с наливным покрытием, гимнастический комплекс, трибуны на 100 зрителей. Для строительства стен были использованы сэндвич панели, сделана собственная кухня, с возможностью приготовления разнообразной пищи для спортсменов разного возраста, сделаны комнаты отдыха для размещения 26 человек. ', NULL, 'The construction consists of: the building of covered with artificial ice rink, open planar structures (stadium with stands), modular boiler plant, transformer substation.&lt br&gt &lt br&gt The building is a two-story indoor ice rink and has a rectangular shape in plan with dimensions in axes 81,5 ? 40,2 . The construction of the building volume isп 28,787.3 cubic meters, the total area is 3596.0 square meters&lt br&gt &lt br&gt &lt br&gt &lt br&gt - On the ground floor: a sports gym with an ice field and grandstands for 204 seats, gym for weightlifting and general physical training, lobby, office, there is a cloakroom, drying, placing the point of skates, pantry, coaching offices, lockers, security room, medical room, judges room, inventory, room for ice resurfacer combines, household, technical and auxiliary facilities.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt - On the second floor are located: a methodical study, dance class, cafeteria, radio room, administrative, household, utility rooms.&lt br&gt &lt br&gt &lt br&gt In the area of planar structures are located sports stadium, designed for training and competition. The complex of sports facilities at the stadium includes the following facilities: football field with artificial turf, two circular and four straight running tracks with an oil coating, gym complex, stands for 100 spectators.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt For the construction of the walls were used sandwich panels, there was made a kitchen, with the possibility of preparing variety of food for athletes of all ages, rest rooms to accommodate 26 people.', 'Новоспасский район', 'Ульяновская область', NULL, 'пгт Новоспасское', 'Novospasskoe', 'Ульяновская область, пгт Новоспасское, ул. 70 лет ВЛКСМ,  1/б', 'Ulyanovsk region, Novospasskiy area, working village Novospasskoe Street. 70 years of the Komsomol, № 1-B', 73629151, 'Развитие ФК и спорта в РФ (2006-2015)', 'с