INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (17959, 'ФОК с плавательным бассейном', 'Sports and fitness comlex with a swimming pool', true, 'Физкультурно-спортивный комплекс направлен на развитие новых видов и форм занятий спортом, физкультурно-оздоровительными и досуговыми занятиями среди всех возрастных и социальных групп населения, от профессиональных спортсменов до любителей: - проведение различных соревнований, в том числе юношеских и молодежных  - подготовка спортсменов по нескольким видам спорта  - проведение занятий воспитанников детских и юношеских школ  - организация свободного плавания для жителей населенного пункта  - проведение развлекательных мероприятий &#40 детские праздники и т.д.&#41   - организация питания для посетителей &#40 буфет&#41   - использование населением прилегающей асфальтированной площадки для активного отдыха &#40 катания на роликах, велосипедах и т.д.&#41 . Даты крупных соревнований и значимых событий: 21.02.2015г. - соревнования по плаванию, посвященные Дню защитника Отечества в 2015 году  25.04.2015г. - открытые соревнования по плаванию, посвященные Всемирному Дню здоровья в 2015 году  08.05.2015г. - соревнования по плаванию, посвященные 70-летию Победы в Великой Отечественной войне 1941-1945 годов в 2015 году. ', 'Автономное теплоснабжение &#40 модульная котельная, 2 котла марки Riello RTQ мощностью 1074 кВт каждый&#41 . Отопление бассейнов – воздушное, совмещено с вентиляцией, подогрев полов обходных дорожек. Автоматическая дезинфекция воды в бассейне обеспечивается установкой Bayrol Analyt путем сравнения заданных параметров с показаниями фактических параметров &#40 осуществляет водоподготовку бассейна путем дозирования хлора &#40 Cl&#41   измеряет и регулирует показатель хлора &#40 Cl&#41 , водородный показатель &#40 pH&#41   измеряет окислительно-восстановительный показатель &#40 Redox&#41 , температуру воды в бассейне. Высокая микропроцессорная технология, низкое потребление химреагентов&#41 . Станция ультрафиолетового обеззараживания воды ультрафиолетовым излучением. ', 'Sports complex is aimed at the development of new types and forms of sports, a fitness and leisure pursuits among all age and social groups, from professional athletes to amateurs: &lt br&gt - Conducting various competitions, including junior and youth, &lt br&gt - Preparation of athletes in several sports, &lt br&gt - Conducting training of children from schools and youth, &lt br&gt - The organization of free swimming for the residents of the village, &lt br&gt - Conducting entertainment events (children''s events, etc.), &lt br&gt - Catering for visitors (buffet), &lt br&gt - The use of the population of adjacent asphalt playground for outdoor activities (skating, biking, etc.).', 'Independent heating (modular boiler, 2 boilers brand Riello RTQ capacity of 1074 kW each).&lt br&gt &lt br&gt Pool heating - air, combined with ventilation, underfloor heating of circuitous paths.&lt br&gt &lt br&gt &lt br&gt Automatic disinfection of pool water by installing Bayrol Analyt by comparing the given parameters with the testimony of the actual parameters (performs pool water treatment by dosing chlorine (Cl)  measures and controls the rate of chlorine (Cl), the pH value (pH)  measures the redox indicator (Redox ), the temperature of the water in the pool. High microprocessor technology, low consumption of chemicals).&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt Station of ultraviolet water disinfection by ultraviolet radiation.', 'г.о. Октябрьск', 'Самарская область', NULL, 'г. Октябрьск', 'Oktyabrsk', 'Самарская область, г. Октябрьск, ул. Куйбышева, 22', 'Samara region, Borough Oktjabrsk, street Kuibyshev, 22', 36718000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2008-01-14', '2013-11-30', 40000000, '10:00-20:00', NULL, NULL, NULL, 'fsk.okt@yandex.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'аквааэробика, бокс, восточное боевое единоборство, дзюдо, настольный теннис, плавание, спортивная аэробика, фитнес-аэробика', 48.6664, 53.17, 2);
