INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (19657, 'ФОК с бассейном', 'Sports complex with a swimming pool', true, 'Физкультурно-спортивный комплекс с бассейном.  В середине сентября открылся спорткомплекс в поселке Кадом Рязанской области В рабочем поселке Кадом Рязанской области заканчилось строительство физкультурно-спортивного комплекса с бассейном. По сообщению отдела спортивных мероприятий и развития видов спорта спорткомитета Рязанской области, объект был открыт 13 сентября 2015 года. В состав физкультурно-спортивного комплекса входит 25-метровый бассейн с шестью дорожками для плавания и несколько тренировочных залов: универсальный зал размером 42х24 м для игровых видов спорта, тренажерный зал и спортзал для общефизической подготовки.  Финансирование строительства спортивного объекта в рамках реализации Федеральной целевой программы «Развитие физической культуры и спорта в Российской Федерации на 2006–2015 годы» составило 90 млн рублей. ', 'Применены сендвич-панели типа «Вентал-С» толщиной 150 мм, обеспечивающие тепло- и звукоизоляцию, высокую огнестойкость, долговечность  небольшой вес  легкость сборки Кровля утепленная по металлическому настилу. Покрытие кровли – основной водоизоляционный ковер Rockmembrane. Наружные стены вспомогательного блока  - из силикатного кирпича с наружным утеплением и облицовкой керамгранитом. Окна – из переплетов ПВХ с двухкамерным стеклопакетом. Витражи – из алюминиевых переплетов с двухкамерным стеклопакетом. На объекте предусмотрена газовая блочно-модульная котельная полной заводской готовности, предназначенная для отопления, теплоснабжения и горячего водоснабжения. спортивный зал 41,64*24 м,  бассейн 29,82*18 (чаша - 25*11,0) тренажерный зал 87,5 м2 спортзал для общефизической подготовки  -96,3 м2 конференц-зал – 61,75 м2 ', 'Sports complex with swimming pool.', 'Applied sandwich panels such as &quot Ventana C&quot  150 mm, providing thermal and acoustic insulation, high fire resistance, durability  low weight  ease of assembly&lt br&gt &lt br&gt &lt br&gt &lt br&gt Insulated metal roofing on the deck. Coverage of the roof - the main waterproofing carpet Rockmembrane.&lt br&gt &lt br&gt &lt br&gt The outer walls of the auxiliary unit - of silica brick with external insulation and cladding granite.&lt br&gt &lt br&gt Windows - of bindings PVC double glazing. Stained glass - aluminum casements with double glazing.&lt br&gt &lt br&gt &lt br&gt &lt br&gt At the facility there is gas modular boiler of full factory readiness, intended for heating, heat and hot water.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt sports gym 41.64 * 24 m,&lt br&gt &lt br&gt Pool 29.82 * 18 (bowl - 25 * 11.0)&lt br&gt &lt br&gt gym 87.5 m2&lt br&gt &lt br&gt &lt br&gt gym for physical training -96.3 m2&lt br&gt &lt br&gt &lt br&gt conference room - 61.75 m2', 'Кадомский район', 'Рязанская область', NULL, 'пгт Кадом', 'village Kadom', 'Рязанская область, пгт Кадом, ул. Советская', 'Ryazan region, working village Kadom, Sovetskaya', 61606151, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2013-10-01', '2015-09-13', 90000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'плавание', 42.4613, 54.5563, 2);
