INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (21393, 'ДЮСШ по футболу «Олимп»', 'Youth football school «Olymp»', true, 'Объект предназначен для организации учебно-тренировочного процесса для воспитанников ДЮСШ, проведения уроков физической культуры учащихся МБОУ «Гимназия», проведения спортивно-массовых мероприятий по месту жительства для местных жителей микрорайонов Чусовского района и проведение занятий оздоровительного характера взрослого населения.', 'Основание футбольного поля с искусственным покрытием имеет дренаж &#40 щебень - наилучший дренажный материал&#41 , исключающий образование луж на поверхности футбольного поля при любых погодных условиях. Монтаж основания должен обеспечить однородность и равномерность слоёв конструкции основания, что даёт равномерную усадку основания по всей его площади и исключает возникновение неровных участков на поверхности футбольного поля. Система водоотвода, являющаяся составной частью конструкции основания, обеспечивает отвод воды с поверхности футбольного поля и из конструкции основания по принципу обратного фильтра. Отвод воды с поверхности футбольного поля обеспечивают высокие дренажные свойства самого искусственного покрытия, имеющего дренажные отверстия, количество и размеры которых определяют его пропускную способность. Щебёночное основание выполнено трёхслойным, где каждый последующий слой расклинивает и частично выравнивает предыдущий. Под щебёночное основание укладывается геотекстиль &#40 синтетический нетканый материал&#41 , который монтируется на грунтовое основание. Геотекстиль армирует всю конструкцию в целом, а также предотвращает процесс диффузии слоёв, что может вызвать возникновение неровных участков на поверхности футбольного поля. ', NULL, NULL, 'Чусовской район', 'Пермский край', NULL, 'г. Чусовой', 'Chusovoy', 'Пермский край, г. Чусовой, ул. Сивкова, 8/а', NULL, 57658101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2012-10-31', '2015-07-31', 21870080, '08:00-20:00', NULL, NULL, NULL, 'sportschool-olimp@yandex.ru', 'футбольное поле ', '8(34256)43209 ', NULL, 'мини-футбол, футбол', 57.8185, 58.2652, 132);
