INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (21949, 'Стадион ДЮСШ «Фаворит»', 'Stadium of youth sports school «Favorit»', true, 'Стадион, на территории которого размещены открытые физкультурно-оздоровительные сооружения, а именно: • поле для игры в хоккей на траве, как основное использование. Данное поле можно использовать для игры в футбол при замене ворот на футбольные 91,4 х 55 м  • площадка для игры в хоккей с шайбой на льду. В летнее время данную площадку можно использовать для игр в волейбол, баскетбол или теннис, общей площадью 38 х 18 м • детская спортивно-игровая площадка представляет собой площадку, ориентированную на активный отдых детей двух основных возрастных групп: 3-6 лет и 7-13 лет. Даты крупных соревнований и значимых событий: 09 мая 2015 г. &#40 в рамках торжественного открытия стадиона&#41  турнир по хоккею на траве среди девушек, посвященный 70-ой годовщине Победы в Великой Отечественной войне.', 'Проектное решение выполнено в соответствии с установленными обязательными технологическими требованиями и исходя из сложившейся градостроительной ситуации. Технологическая схема размещения полей отвечает всем существующим нормам и правилам.', NULL, NULL, 'Выборгский район', 'Ленинградская область', NULL, 'г. Выборг', 'Vyborg', 'Ленинградская область, г. Выборг, ул. Выборгская, 27', NULL, 41615101, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2014-06-18', '2014-12-19', 54322178, NULL, NULL, NULL, NULL, 'sportotdel_vbg@mail', 'стадион', '8(81378)316-00', 'http://www.favorit-detsport.com/', 'бадминтон, баскетбол, волейбол, футбол, хоккей, хоккей на траве', 28.7431, 60.707, 138);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (22942, 'Стадион «Центральный»', 'Stadium «Tsentralny»', true, 'Спортивный объект находится в центре города, имея транспортную доступность способствует развитию детско-юношеского и массового футбола, футбола среди инвалидов, лиц с ограниченными возможностями здоровья и других групп населения, формирует культуру здоровья, укрепление здоровья по средствам занятий физическими упражнениями. Реконструкция данного спортивного сооружения позволит проводить в Великом Новгороде соревнования различных рангов на высоком организационном уровне. Даты крупных соревнований и значимых событий: Торжественное мероприятие открытия стадиона в декабре 2015 года.', 'В соответствии с заданием на проектирование в составе 1 этапа реконструкции стадиона предусматривается:  Футбольное поле размером 120x80 м2  площадь игрового поля 105x68 м2. Минимальные расстояния от линий, ограничивающих игровое поле, до трибун и разметка принята согласно правилам игры. Игровое поле предусмотрено с искусственным покрытием, системой жидкостного подогрева, дренажной системой и системой орошения соответствуют международным стандартам и требованиям ФИФА.  Для зрителей на стадионе во время проведения футбольных матчей предусматриваются трибуны с общей численностью 3223 мест, 50 мест для VIP персон. Трибуны оснащены индивидуальными откидными креслами. Расстояние между рядами кресел 800мм. Сиденья должны быть прочными, огнестойкими и способными выдержать перемены местного климата без излишнего износа и выцветания. Над трибуной предусматривается козырек-навес для защиты зрителей от дождя и палящего солнца. Навес закрывает 50&#37  зрительских мест. Между игровым полем и трибунами предусматривается 16 мест для инвалидов колясочников.  Все оборудование и инвентарь для реконструкции стадиона «Центральный» принимался в соответствии «Табелю оснащения спортивных сооружений массового пользования спортивным оборудованием и инвентарем», утвержденным приказом от 26 мая 2003г. №345, Государственным комитетом Российской Федерации по физической культуре и спорту. Проектируемый стадион оснащен современным оборудованием, мебелью, инвентарем, которые должны быть сертифицированы и разрешены к применению на территории Российской Федерации. Площади технологических помещений соответствуют действующим санитарно-гигиеническим нормам и правилам. Инженерное оснащение вспомогательных помещений стадиона включает, в соответствии с требованиями СП, устройства освещения, вентиляции, водоснабжения, канализации, отопления, электроснабжения, пожаротушения, средств связи.', NULL, NULL, 'Город Великий Новгород', 'Новгородская область', NULL, 'г. Великий Новгород', 'Veliky Novgorod', 'Новгородская область, г. Великий Новгород, Парк 30-Летия Октября, стр. 1', NULL, 49701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2015-01-12', '2015-12-01', 308024500, NULL, NULL, NULL, NULL, NULL, 'стадион', NULL, NULL, 'футбол', 31.2942, 58.5336, 98);
