INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (22234, 'Футбольное поле с искусственным покрытием', 'Football field with artificial turf', true, 'Футбольное поле предназначено для проведения учебно-тренировочных процессов и спортивных мероприятий,  развития детско-юношеского и массового футбола.  Увеличение граждан регулярно занимающихся футболом, футбол для инвалидов, лиц с ограниченными возможностями здоровья и других групп населения', 'Строительство велось с использованием типовых технологий.', NULL, NULL, 'Хасавюртовский район', 'Республика Дагестан', NULL, 'с. Эндирей', 'Endirey', 'Республика Дагестан, с. Эндирей, ул. Радуга, 1', NULL, 82654409, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2014-01-14', NULL, 16221000, NULL, NULL, NULL, NULL, '676392@mail.ru', 'футбольное поле ', NULL, NULL, 'мини-футбол, футбол', 46.661, 43.1831, 128);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (22240, 'ДЮСШ по футболу', 'Football youth sports school', true, 'По программе развития футбола Российского футбольного союза приобретено и установлено искусственное футбольное покрытие. Реконструкция данного спортивного сооружения позволит проводить в Иркутске  соревнования различного уровня на высоком организационном уровне. Приобщить населения к занятиям физической культурой и спортом.    - проведение  занятий воспитанников ОСДЮСШ по футболу и ДЮСШ  «Рекорд»  - проведение спортивных мероприятий среди школ Иркутска и Иркутской области  - проведение соревнований городского, областного, регионального уровней  - обеспечение доступа к местам занятий ФК и спорта для населения города  - обеспечение доступа к местам занятий ФК и спорта для организаций и предприятий города. ', 'Строительство велось с использованием типовых технологий.', NULL, NULL, 'Город Иркутск', 'Иркутская область', NULL, 'г. Иркутск', 'Irkutsk', 'Иркутская область, г. Иркутск, бул. Рябикова, 13/г', NULL, 25701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2013-01-14', NULL, 11257000, '08:00-20:00', NULL, NULL, NULL, 'Fsio.irk@yandex.ru', 'футбольное поле ', '8(3952)592-162', NULL, 'футбол', 104.2016, 52.2687, 140);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (22251, 'ДЮСШ по футболу «Металлург»', 'Youth sports school «Metallurg»', true, 'Обновленный  стадион «Металлург» стал новым импульсом для развития футбола &#40 особенно детского&#41 , как в нашем городе, так и в регионе. Футбольное поле с искусственным покрытием - результат реализации государственной программы «Развитие физической культуры и спорта на 2006-2015 годы» с участием регионального и городского бюджета. Кроме того запуск в эксплуатацию футбольного поля с искусственным покрытием и легкоатлетическими дорожками позволил решить проблемные вопросы по развитию футбола и легкой атлетики в Центральном округе города Братска,   связанные с обеспечением и созданием комфортных климатических, доступных и современных условий для занятий футболом и легкой атлетикой, проведением соревнований различного уровня. Главным результатом реализации этого совместного проекта  стала популяризация футбола, улучшение инфраструктуры и материально-технической базы ДЮСШ, а также массового  и профессионального футбола. Значительно увеличилось количество братчан, приходящих на стадион. Многие городские организации и предприятия пользуются услугами стадиона для организации занятий и игр по футболу. Футбол объединяет людей различных возрастных и социальных категорий, способствует росту патриотизма братчан. Сегодня в городе футболом занимаются свыше тысячи братчан, начиная от младших школьников и до ветеранов, стоявших у истоков братского футбола. Ежегодно в городе проводится более 30 городских и областных мероприятий, связанных с этим видом спорта. Футбольная школа  &#40 ДЮСШ по футболу при  МАУ ФК «Сибиряк»&#41  – одна из самых многочисленных в городе, ее посещает более 600 детей. Даты крупных соревнований и значимых событий:  С 2010 по 2014 год проходили домашние игры футбольного клуба «Сибиряк» в Первенстве России по футболу II дивизиона зоны «Восток» С 2014 года и по настоящее время проводится игры Первенства России по футболу среди любительских футбольных команд 3-го дивизиона зоны «Сибирь». Первая лига/Высшая лига.   Ежегодно с мая по октябрь. Летнее первенство города Братска по футболу среди КФК. Первая лига/Высшая лига. Ежегодно с декабря по март. Зимнее первенство города Братска по футболу среди КФК. Ежегодно апрель-май. Первенство города Братска по футболу на приз клуба «Кожаный мяч». Ежегодно июнь-август. Первенства города по футболу Братска среди дворовых команд. Июнь 2012. 2-й региональный этап Международного фестиваля «Локобол-2012» по футболу среди юношей 2001-2003гг.р. 2012.Первенство России МОО СФФ «Сибирь по футболу среди юношей 1993-94гг.р. 03.07.2011.Товарищеский матч по футболу среди сборных команд ветеранов футбола г. Братска и сборной СССР-России. 12 июня 2011-2013гг.Спортивный праздник, посвященный Дню России.  ', 'Искусственное покрытие для футбольного поля является очень популярным  в связи с тем, что это покрытие обладает огромным количеством преимуществ. В первую очередь, искусственное поле не требует тщательного ухода и выполнения восстановительных работ  специальная дренажная система способствует быстрому уходу дождевой воды, без образования луж  не требуется приобретение и применение специальной техники Данное покрытие долговечное и надежное, потому что во время длительного времени использования оно не втаптывается и не выбивается, а также не теряет своего цвета. Технология укладки искусственной травы на футбольных полях по техническим характеристикам приближает ее к параметрам натуральных газонов. Покрытие имеет высокие технические характеристики - обеспечивает хороший отскок мяча, не затрудняет его скольжение, что позволяет раскрыть и улучшить игровые свойства спортсменов  обладает хорошей эластичностью и упругостью, что обеспечивает долговечность покрытия, а также снижает уровень вероятного травматизма.', NULL, NULL, 'Город Братск', 'Иркутская область', NULL, 'г. Братск', 'Bratsk', 'Иркутская область, г. Братск, ул. Комсомосльская, 35/а', NULL, 25714000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2009-08-10', '2011-09-27', 25450000, NULL, NULL, NULL, NULL, 'mypark@mail.ru', 'стадион', '457-472, 414-054', 'http://fcsibiryak.ru/', 'лёгкая атлетика, мини-футбол, футбол', 101.612, 56.1576, 141);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (22283, 'Модульный бассейн на территории Дворца спорта «Юность»', 'Modular pool in the Palace of Sports «Yunost»', true, '      Введение в эксплуатацию нового модульного бассейна имеет высокую социальную значимость для города Иркутска и Иркутской области. Организация учебно-тренировочного процесса ведущими специалистами по плаванию региона позволило: •привлечь более 500 детей и подростков города Иркутска к систематическим занятиям спортивным плаванием  •осуществлять учебно-тренировочный процесс с обучающимися в двух современных спортивных и тренажерном залах, бассейне, оснащенных новейшим специализированным оборудованием и инвентарем для пловцов  •подготовить: - трех членов юниорской сборной команды Российской Федерации  - четырех мастеров спорта России  - победителя первенств Европы 2013,2014 годов &#40 Пермякова Елизавета&#41 ,  международных азиатских игр 2013 года &#40 Брянский Алексей&#41   10 победителей первенств и этапов Кубков России, 30 победителей и призеров чемпионатов и первенств Сибирского федерального округа, более 50 чемпионов и рекордсменов Иркутской области  •организовать ежегодно проведение более 10 соревнований, в том числе всероссийского уровня, •провести 10 научно-практических конференций, семинаров спортивных судей, методических занятий по совершенствованию учебно-тренировочного процесса  •привлечь специалистов по спортивной медицине для медико-биологического сопровождения и улучшения учебно-тренировочного процесса по плаванию. Даты крупных соревнований и значимых событий: Российский уровень: •Всероссийские соревнования по плаванию  «Кубок Сибири»,  23-25 декабря 2014г. Региональный уровень: •Чемпионат и первенство Иркутской области, 07-09 февраля 2013г. •Первенство Иркутской области, 12-14 марта 2014г. Муниципальный уровень: •Открытое первенство г. Иркутска среди школьников, 19-20 октября 2012г. •Первенства г. Иркутска среди обучающихся  МБОУ, МАОУ г. Иркутска в рамках Спартакиады школьников, 19 апреля 2014 г., 15 апреля 2015г. ', 'При строительстве модульного бассейна применены следующие значимые современные технологии:  •в системе  вентиляции -  - пластинчатые рекуператоры для режима энергосбережения,  - осушители воздуха  и новые типы хладогентов для улучшения микроклимата в помещениях  •в системе водоподготовки  установлен озоногенератор для  улучшения качества воды   •в чаше бассейна, впервые в городе Иркутске,  применена гидроизоляция Mopelastic с линейным расширением до 5 мм и трехкомпонентный заполнитель для швов керамической плитки Kerapocsu с высокой стойкостью к агрессивным средам для более длительной и качественной эксплуатации бассейна. ', NULL, NULL, 'Город Иркутск', 'Иркутская область', NULL, 'г. Иркутск', 'Irkutsk', 'Иркутская область, г. Иркутск, ул. Некрасова, 4/2 ', NULL, 25701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2010-10-20', '2011-12-29', 175070000, '08:00-21:00', NULL, NULL, NULL, 'dvorecunost@bk.ru', 'бассейн', NULL, 'www.dvorecunost.ru', 'плавание', 104.2838, 52.2884, 142);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (22320, 'Комплекс зимних видов спорта', 'Winter sports complex', true, 'Строительство парно-кресельной канатной дороги на спорткомплексе трамплинов улучшит качество учебно-тренировочного  процесса спортсменов двух спортивных школ &#40 ДЮСШ по прыжкам на лыжах с трамплина и СДЮСШОР по горнолыжному спорту&#41    позволит привлекать спортсменов на тренировочные сборы из разных регионов России, проводить соревнования на более высоком организационном уровне. С вводом в эксплуатацию  канатной  парной кресельной дороги увеличится поток туристов &#40 будет задействован северный склон горы Югус&#41 .', 'Стандартный проект, выполненный с учетом местных горно-геологических условий', NULL, NULL, 'Междуреченский г.о.', 'Кемеровская область', NULL, 'г. Междуреченск', 'Mezhdurechensk', 'Кемеровская область, пос. Сыркаши, левый берег р. Томь', NULL, 32725000, NULL, 'строительство', '2006-02-06', '2016-12-31', 37800000, NULL, NULL, NULL, NULL, 'czvs.ugus@gmail.com', 'комплекс горнолыжный', '8(38475) 4-52-85', NULL, 'горнолыжный спорт, прыжки на лыжах с трамплина, сноуборд', 88.0874, 53.6722, 143);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (22369, 'ДЮСШ №1', 'Yuth sports school #1', true, 'Стадион «Металлург» с футбольным полем с искусственным покрытием, административным зданием, г.Белово является одним из центров спортивной работы в районе. Стадион «Металлург» выполняет свою социальную роль, исходя из целей, задач, видов деятельности и требований к системе дополнительного образования детей спортивной направленности. Стадион «Металлург», как одно из спортивных сооружений МБОУ ДЮСШ-1 ведет работу по сохранению и укреплению здоровья, создаёт условия для систематических занятий спортом, привлекает детей и подростков к ведению здорового образа жизни, к занятиям спортом, для профилактики асоциального поведения детей, обеспечивает доступность для занятий любимым видом спорта, удовлетворяет профессиональную потребность тренеров-преподавателей заниматься избранным видом деятельности. ', 'Устройство основания поля, устройство накопительных колодцев и устройство дренажной системы, укладка последнего слоя щебнем 20 х 40, фракция 5 х 25. Уложено современное искусственное покрытие футбольного поля для проведения учебно-тренировочного и соревновательного процессов.&nbsp &nbsp ', NULL, NULL, 'Беловский г.о.', 'Кемеровская область', NULL, 'г. Белово', 'Belovo', 'Кемеровская область, г. Белово, ул. Пролетарская, 1', NULL, 32707000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2013-12-25', '2015-08-01', 18871000, '08:00-20:00', NULL, NULL, NULL, NULL, 'футбольное поле ', '8-384-52-2-33-38', NULL, 'футбол', 86.2911, 54.4276, 144);
