INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (23530, 'Многофункциональный зал', 'Multifunctional gym', true, 'Тип  спортивного объекта позволяет проводить  спорт мероприятия  для детей, начиная с 5- лет, для взрослого населения и людей пенсионного возраста.  Стало возможным предоставить жителям района  условия для развития способностей и достижения спортивных успехов. Описание объекта с точки зрения его социальной значимости:  - осуществление спортивно-оздоровительной, воспитательной работы среди детей и подростков, направленной на укрепление их здоровья  - улучшение состояния здоровья, включая физическое развитие  - формирование морально-этических и волевых качеств  - пропаганда здорового образа жизни средствами физической культуры и спорта  - привлечение максимально возможного числа детей и подростков к систематическим занятиям спортом с целью снижения уровня преступности в Атнинском районе  - обеспечение подготовки спортсменов. Помещения оснащена современным оборудованием, инвентарём, мебелью. Даты крупных соревнований и значимых событий: 2015 году проведены следующие мероприятия:  - Зональный этап военно-спортивной игры «Зарница 2015»  - Открытое соревнования по национальной борьбе в память легендарного борца «Бэрэзэ Хафиз».  ', 'При строительстве объекта применялись современные технологии, материалы и оборудование.  Технологические решения, примененные при строительстве, соответствуют требованиям экологических, санитарно-гигиенических, противопожарных и других норм, действующих на территории РТ. В здании имеются естественная и механическая приточно-вытяжная вентиляция, приборы учета тепло - и водопотребления. ', NULL, NULL, 'Атнинский район', 'Республика Татарстан', NULL, 'с. Большая-Атня', 'Bolshaya Atnya', 'Республика Татарстан, с. Большая Атня, ул. Советская, 2/б', NULL, 92613408, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2012-10-15', '2014-10-30', 20000000, '08:00-23:00', '08:00-23:00', '08:00-23:00', NULL, 'Odms.atnja@mail.ru', 'зал спортивный', '8(84369)21021', 'https://edu.tatar.ru/atnya/page518712.htm/org5664', 'бадминтон, баскетбол, волейбол, мини-футбол, настольный теннис, шахматы, шашки', 49.4403, 56.248, 158);
