INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (23542, 'ДЮСШ № 3 ', 'Youth sports school #3', true, 'Пропаганда физической культуры и спорта среди детей и подростков, популяризация и развитие футбола среди детей, привлечение детей к систематическим занятиям спортом, выявление сильнейших команд и лучших игроков. Даты крупных соревнований и значимых событий: Торжественное открытие, домашний матч в рамках Чемпионата Республики Татарстан по футболу, 01-05.06.2015 г. турнир по футболу среди юношей, посвященного памяти заслуженного работника физической культуры РТ Г.Востокова, сентябрь – октябрь турниры посвященные памяти Белова, В.Колотова, домашние матчи в рамках Чемпионата и Кубка РТ, Первенство РТ по футболу согласно календарю игр в сезоне 2015 г.', 'При строительстве применялся современный дренирующий материал.', NULL, NULL, 'Зеленодольский район', 'Республика Татарстан', NULL, 'г. Зеленодольск', 'Zelenodolsk', 'Республика Татарстан, г. Зеленодольск, ул. Ленина, 5', NULL, 92628101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2014-11-29', '2015-06-30', 16842000, '08:00-21:00', NULL, NULL, NULL, 'dyush3@yandex.ru', 'футбольное поле ', '8(84371) 5-65-73', 'Edu.tatar.ru', 'футбол', 48.4872, 55.8471, 159);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (23548, 'Крытый ледовый каток в микрорайоне 26м', 'Indoor ice skating rink in the neighborhood 26m', true, 'Завершено строительство крытого ледового катка в микрорайоне 26м города Казани. Церемония открытия спорткомплекса состоялась 9 сентября. Спортивный объект представляет собой трехэтажное здание, на базе которого будут проходить тренировки и соревнования по хоккею, фигурному катанию и шорт-треку областного и муниципального уровня. На первом этаже спорткомплекса находится сама ледовая арена со зрительскими  трибунами на 200 человек. На втором этаже размещается тренажерный зал, раздевалки, душевые, хореографический класс и буфет. Третий этаж здания отвели под административно-бытовые помещения. Возведение ледового катка осуществлялось в рамках реализации Федеральной целевой программы «Развитие физической культуры и спорта в РФ на 2006-2015 годы», по которой на строительные работы было выделено 100 млн рублей.   Ледовый каток в Казани построен по Федеральной целевой программе  «Развитие физической культуры и спорта в РФ на 2006-2015 годы». Материалы, которые выйдут по итогам открытия спортивного объекта, можно направить на участие во Всероссийском конкурсе СМИ «Спортивные регионы - Спортивная Россия». Конкурс уже в третий раз проводится Министерством спорта РФ, в этом году тематика – реализация ФЦП «Развитие физической культуры и спорта в РФ на 2006-2015 годы»  в регионах. Направляем Положение о конкурсе и формы  заявок на участие.  Социально значимый объект Молодежного жилого комплекса микрорайона М26 Ново-Савиновского района.    На территории данного микрорайона расположены три  общеобразовательные школы, три дошкольных образовательных учреждения и 48 жилых домов с общим охватом жителей более 9000 человек, что гарантирует целевое круглогодичное использование ледовой арены. Функционирование спортивного объекта будет способствовать дальнейшему развитию в нашем городе таких популярных видов спорта, как хоккей и фигурное катание, и позволит привлечь большее количество детей к систематическим занятиям физической культурой и спортом по месту жительства. ', 'Конструктивная система здания:  Каркас стальной. Основные несущие конструкции - двухшарнирные арки пролетом 45м в количестве 10 шт., установленные с шагом 6м. Фундаменты – ростверки по ж/б сваям. Наружная отделка фасадов – композитные панели. Оконные конструкции, витражи и фасадные двери – из теплого профиля, с заполнением из двухкамерного стеклопакета, с улучшенной шумоизоляцией. Кровля по арочным конструкциям – скатная &#40 цилиндрическая&#41  с покрытием из профлиста.', NULL, NULL, 'Город Казань', 'Республика Татарстан', NULL, 'г. Казань', 'Kazan', 'Республика Татарстан, г. Казань, ул. Гаврилова ', NULL, 92701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2014-08-04', '2015-09-09', 100000000, NULL, NULL, NULL, NULL, 'kazansport@mail.ru', 'арена ледовая', NULL, NULL, 'фигурное катание, хоккей', 49.1479, 55.843, 160);
