INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (23690, 'Шиловская ДЮСШ', 'Shilovskaya Youth sports school', true, 'МУ «Стадион» муниципального образования  – Шиловское городское поселение Шиловского муниципального района Рязанской области расположен в центре р.п. Шилово. Своим местоположением обеспечивает легкую доступность жителям поселка к спортивной инфраструктуре стадиона. Одновременно стадион является местом проведения самых массовых спортивных состязаний и спортивных праздников, проходящих в Шиловском районе. Даты крупных соревнований: Финал кубка Рязанской области по футболу 12.06.2015г., «День физкультурника» август 2015г.', 'Построено новое футбольное поле с искусственным покрытием и дренажной системой', NULL, NULL, 'Шиловский район', 'Рязанская область', NULL, 'пгт Шилово', 'Shilovo', 'Рязанская область, пгт Шилово, ул. Фирсова', NULL, 61658151, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', NULL, '2016-12-29', 8421000, NULL, NULL, NULL, NULL, NULL, 'стадион', NULL, NULL, 'лёгкая атлетика, футбол', 40.8766, 54.3146, 162);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24092, 'Крытый тренировочный каток с искусственным льдом', 'Indoor training rink with artificial ice', true, 'Крытый каток – это многоцелевой спортивный комплекс, в котором можно проводить спортивные соревнования: хоккей, фигурное катание, шорт-трек,  шахматы,  ледовые шоу  выставки и ярмарки, конференции, празднование юбилейных дат организаций и фирм. Услуги, предлагаемые крытым тренировочным катком, являются уникальными для Чугуевского муниципального района и не только. В разрезе социальной значимости – тренировочный каток  является учреждением, как для общего физического развития,  так и для семейного  отдыха. Крытый каток: это двухэтажное здание площадью 4425,1 м2,  зрительских мест 526, площадь хоккейного поля 75,0х36,0 метров.  В больших фойе 1-го, 2-го,  этажей созданы все условия для удобства зрителей. В фойе установлены телевизоры по которым можно посмотреть передачи спортивных каналов. 2-й этаж оборудован комнатами гостиничного типа. Тренажерный зал. Хорошая вентиляция и кондиционирование надежно обеспечивают зрителям комфортные условия в любую погоду.  На базе крытого тренировочного катка открыты отделения по видам спорта: хоккей с шайбой, шорт-трек. К услугам жителей и гостей района – каток для массового катания на коньках. К услугам катающихся - коньки всех размеров, удобные раздевалки, работает гардероб.  Даты крупных соревнований и значимых событий: Январь - «Краевые финальные соревнования юных хоккеистов «Золотая шайба» 1999-2000г.р. среди городских округов. Февраль – «Краевые финальные соревнования юных хоккеистов 1999-2000г.р. среди муниципальных районов края.  ', 'Наружные стены катка и пристроенных технических помещений – сендвич-панели с облицовкой из оцинкованной стали с полимерным покрытием.  Все холодильное оборудование поставленно корейской стороной. При производстве отделочных работ применены современные материалы.  ', NULL, NULL, 'Чугуевский район', 'Приморский край', NULL, 'с. Чугуевка', 'Chuguevka', 'Приморский край, с. Чугуевка, ул. Комарова, 21/а ', NULL, 5655437, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2010-08-19', 60000000, '09:00-22:00', '09:30-22:00', '12:00-21:00', NULL, 'chuguevka-olimp2010@mail.ru', 'арена ледовая', '8(4237)222253', NULL, 'фигурное катание, хоккей', 133.8603, 44.1816, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24100, 'Плавательный бассейн', 'Swimming pool', true, 'Плавательный бассейн с тренажерным и гимнастическим залом. Даты крупных соревнований и значимых событий: Октябрь и июнь. Первенство КК ДЮСШ по плаванию: Ноябрь . Первенство Приморского края по синхронному плаванию:Декабрь . Открытое первенство Партизанского городского округа по плаванию. ', 'Плавательный бассейн с ванной 25х16 м  с переменной глубиной от 1,8 до 2.02 м имеет 6 &#40 шесть&#41  плавательных дорожек оборудованных разделительными поплавками   с волногасителями и пропускной способностью 48 чел в час &#40 вместимость зрительных мест 134 чел единовременно&#41 .', NULL, NULL, 'Партизанский район', 'Приморский край', NULL, 'г. Партизанск', 'Partizansk', 'Приморский край, г. Партизанск', NULL, 5717000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-02-02', '2009-12-31', 40000000, '08:00-21:00', '08:00-21:00', '10:00-18:00', NULL, 'suchan-pool@mail.ru', 'бассейн', '8 (4236) 367-963', NULL, 'водное поло, плавание, синхронное плавание, шейпинг', 133.1265, 43.128, 163);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24162, 'Стадион', 'Stadium', true, 'Объект предназначен для проведения уроков физического воспитания, тренировок, соревнований профессиональных и любительских команд, а также для свободного посещения жителей близлежащих домов. Строительство объекта способствует формированию здорового образа жизни, повышению здоровой культуры населения, развитию массового спорта, в первую очередь детского. Объект формирует сферу общения людей.', 'Применение на футбольном поле покрытия из искусственной травы «Euro Grass M40», на беговых дорожках и спортивных площадках резиновой крошки «Мастерфайбер-спорт»', NULL, NULL, 'Пермский район', 'Пермский край', NULL, 'с. Бершеть', 'Bershet', 'Пермский край, с. Бершеть, ул. Молодежная, 2', NULL, 57646402, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2013-09-09', '2013-12-30', 15222700, '14:00-22:00', '14:00-22:00', '14:00-22:00', NULL, 'dskrasava@mail.ru', 'стадион', NULL, NULL, 'баскетбол, волейбол, лёгкая атлетика, лыжные гонки, мини-футбол, футбол', 56.3616, 57.7307, 133);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24185, 'Бассейн в микрорайоне 2-3 ', 'Swimming pool in district 2-3', true, 'С точки зрения социальной значимости объекта:  проблема муниципального образования особенно актуальна. В городе практически отсутствуют спортивные объекты, соответствующие  современным нормам и требованиям. Нет ни одного плавательного бассейна,  отсутствуют универсальные спортивные сооружения, единственный в городе стадион морально и физически устарел и не подлежит реконструкции. В целом:   обеспеченность населения спортивными объектами  находится на крайне низком уровне  1 июня в городе Чернушке Пермского края был открыт бассейн! В городе Чернушке &#40 Пермский край&#41  прошло открытие спортивного комплекса с 25-метровым плавательным бассейном. Как сообщает Министерство физической культуры и спорта Пермского края, торжественное открытие бассейна состоялось 1 июня 2015 г. Помимо 25-метрового бассейна в спорткомплексе оборудован детский бассейн размером 10 м х 6 м, зал сухого плавания, раздевалки с душевыми кабинами и административно-бытовые помещения. Строительство спортивного объекта проводилось при поддержке Федеральной целевой программы «Развитие физической культуры и спорта в Российской Федерации на 2006-2015 годы». Отметим, что реализация Программы в регионе ознаменовалась также строительством крытых катков в Красновишерске, крытого бассейна в Барде, спортивно-оздоровительного комплекса в Чусовом, ФОКа в Ильинском, межшкольных стадионов в Юрле, Кизеле, Ильинском, Оханске и Усть-Кишерте, а также Федерального центра подготовки по зимним видам спорта «Снежинка» в Чайковском. ', 'Строительство велось с использованием типовых технологических решений.', NULL, NULL, 'Чернушинский район', 'Пермский край', NULL, 'г. Чернушка', 'Chernushka', 'Пермский край, г. Чернушка, ул. Гостева, 1', NULL, 57657101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2013-06-18', '2015-06-01', 150310950, NULL, NULL, NULL, NULL, 'stroy-chern@mail.ru', 'бассейн', NULL, NULL, 'плавание', 56.0772, 56.5078, 164);
