INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24473, 'Центр большого тенниса', 'Lawn Tennis centre', true, 'Академия большого тенниса в Рязани состоит из трех блоков: двухэтажного сервисного центра, четырехэтажного гостиничного комплекса и теннисного блока с легкоатлетическими дорожками на 200м и мини-футбольным полем. Всего оборудовано 18 кортов. 12 из них расположены под открытым небом: 8 с грунтовым покрытием и 4 – с искусственным. Другие 6 теннисных кортов с искусственным травяным покрытием расположены внутри одноэтажного сооружения каркасного типа размером 123х38 м. Кроме того, внутри здания обустроены зрительские трибуны, рассчитанные на 432 человека.', NULL, NULL, NULL, 'Город Рязань', 'Рязанская область', NULL, 'г. Рязань', 'Ryazan', 'Рязанская область, г. Рязань', NULL, 61701000, NULL, 'строительство', NULL, '2015-08-31', 300000000, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'теннис', 39.7352, 54.6291, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24531, 'Физкультурно-спортивный комплекс', 'Sports complex', true, 'Спортивное сооружение включает в свой состав универсальный зал для игровых видов спорта размером 32х16 м, тренажерный зал, раздевалки, душевые кабины и административные помещения. На базе спорткомплекса будут проводиться тренировки по мини-футболу, баскетболу, волейболу и некоторым видам единоборств. Строительство физкультурно-спортивного комплекса в Обшаровке проводилось в рамках реализации Федеральной целевой программы «Развитие физической культуры и спорта в Российской Федерации на 2006–2015 годы». ', NULL, NULL, NULL, 'Приволжский район', 'Самарская область', NULL, 'с. Обшаровка', 'Obsharovka', 'Самарская область, с. Обшаровка', NULL, 36636408, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2015-08-28', 26000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, волейбол, единоборства (разное), мини-футбол', 48.8647, 53.1191, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24548, 'Ледовый дворец «Чебоксары Арена»', 'Ice palace «Cheboksary-Arena»', true, 'Спортивный комплекс c крытой ледовой ареной является одним из самых крупных спортивных сооружений в республике и рассчитан на 7,5 тысяч зрителей. Помимо ледовой арены в спорткомплексе функционируют раздевалки, душевые кабины, тренажерный зал, медико-восстановительный центр, современный конференц-зал, кафе, административно-бытовые помещения и подземная автостоянка.   Строительство Ледового дворца осуществлялось при софинансировании из федерального бюджета в рамках реализации Федеральной целевой программы &quot Развитие физической культуры и спорта в Российской Федерации на 2006-2015 годы&quot . Следует отметить, что Республика Чувашия стала одним из первых регионов, включившихся в реализацию данной программы. За это время на территории республики на условиях софинансирования было построено и реконструировано более 50 объектов спортивной инфраструктуры', NULL, NULL, NULL, 'Чебоксарский район', 'Республика Чувашия', NULL, 'г. Чебоксары', 'Cheboksary', 'Республика Чувашия, г. Чебоксары', NULL, 97701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2015-06-03', 270000000, NULL, NULL, NULL, NULL, NULL, 'арена ледовая', NULL, NULL, 'фигурное катание, хоккей', 47.2399, 56.1387, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24786, 'Физкультурно-оздоровительный комплекс', NULL, true, 'Спортивное сооружение с универсальным игровым залом служит удовлетворению потребностей населения в систематических занятиях физической культурой и спортом, его оздоровление, формирование физически развитых и здоровых граждан, служит основой подготовки спортсменов.', 'Фундаменты – мелкого заложения стаканного типа. Несущие конструкции – металлический каркас. Ограждающие конструкции – панели типа «Сэндвич»', NULL, NULL, 'Заполярный район', 'Ненецкий АО', NULL, 'с. Нижняя Пеша', NULL, 'Ненецкий автономный округ, с. Нижняя Пеша', NULL, 11811000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-09-01', '2014-09-11', 12820100, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, волейбол, гиревой спорт, лыжные гонки, настольный теннис', 47.7553, 66.7546, 169);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24556, 'Стадион «Олимпийский»', 'Stadium «Olympiysky»', true, 'По реконструкции тренировочного легкоатлетического поля и подтрибунных помещений стадиона «Олимпийский» разработана проектная и рабочая документация, согласно которой предусмотрено выполнены следующие виды работ: - реконструкция тренировочного легкоатлетического поля с заменой синтетического покрытия беговых дорожек и секторов. - реконструкция подтрибунных помещений стадиона «Олимпийский» и строительство новых помещений для размещения медицинских кабинетов, помещений для проведения пресс-конференций и допинг-контроля и для проведения учебно-спортивной работы. Реконструкция началась в июне 2014 г., а к 1 июня 2015 года работы по реконструкции были завершены и состоялось открытие стадиона. ', NULL, NULL, NULL, 'Чебоксарский район', 'Республика Чувашия', NULL, 'г. Чебоксары', 'Cheboksary', 'Республика Чувашия, г. Чебоксары', NULL, 97701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2014-06-02', '2015-06-01', 50000000, NULL, NULL, NULL, NULL, NULL, 'стадион', NULL, NULL, 'футбол', 47.2399, 56.1387, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24595, 'Стадион', 'Stadium', true, '31 августа состоялось открытие  нового школьного стадиона на улице Парковой в городе Губахе Пермского края. На территории спортивного объекта размещается футбольное поле, волейбольная, баскетбольная и хоккейная площадки, а также легкоатлетический сектор с прыжковыми ямами и беговыми дорожками по 100 и 200 м. Деньги на строительство школьного стадиона в Губахе выделялись из внебюджетных источников, из краевого и федерального бюджета в рамках реализации Федеральной целевой программы «Развитие физической культуры и спорта в Российской Федерации на 2006-2015 годы». Сумма, выделенная на возведение объекта из федерального бюджета составила 4 200,00 тыс. рублей. ', NULL, NULL, NULL, 'г.о. город Губаха', 'Пермский край', NULL, 'г. Губаха', 'Gubakha', 'Пермский край, г. Губаха', NULL, 57717000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, NULL, 4200000, NULL, NULL, NULL, NULL, NULL, 'стадион', NULL, NULL, 'баскетбол, волейбол, лёгкая атлетика, футбол, хоккей', 57.5545, 58.8371, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24603, 'Центра  пляжных видов спорта СДЮСШОР им. А.Савина', 'A. Savin Centre of beach sports youth sports school of Olympic reserve', true, '29 августа 2015 года состоялось долгожданное открытие Центра  пляжных видов спорта в городе Обнинске Калужской области. Спорткомплекс, появившийся на базе СДЮСШОР имени Александра Савина, отвечает всем мировым стандартам, что позволит проводить в нем соревнования не только всероссийского, но и международного уровня.  На церемонии открытия присутствовали исполняющий обязанности губернатора области Анатолий Артамонов, заместитель министра спорта РФ Павел Колобков,  генеральный директор Всероссийской Федерации волейбола Андрей Горбенко, а также ветераны волейбола, призеры Олимпийских игр  Александр Савин и Ярослав Антонов.   Павел Колобков поприветствовал гостей и участников спортивного праздника и передал слова благодарности от министра спорта В.Л. Мутко всем тем, благодаря кому в городе появился столь крупный и значимый объект. Он отметил, что спортивная площадка является одной из лучших в мире и благодаря ее универсальности на базе центра можно будет проводить тренировки и соревнования не только по волейболу, но также по футболу и теннису.   В состав спорткомплекса входят два крытых зала, зал общефизической подготовки, раздевалки, тренерские комнаты, медицинский кабинет и помещения инженерного обеспечения. На строительство объекта из федерального бюджета в рамках реализации Федеральной целевой программы «Развитие физической культуры и спорта в Российской Федерации на 2006-2015 годы» была выделена субсидия в размере 45 млн рублей.  Церемония открытия Центра  пляжных видов спорта завершилась ярким выступлением команд фитнес-аэробики и награждением победителей и призеров открытого Кубка губернатора Калужской области по пляжному волейболу, игры которого проводились  на базе нового спортивного объекта. ', NULL, NULL, NULL, 'Город Обнинск', 'Калужская область', NULL, 'г. Обнинск', 'Obninsk', 'Калужская область, г. Обнинск', NULL, 29715000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2015-08-25', 45000000, NULL, NULL, NULL, NULL, NULL, 'зал спортивный', NULL, NULL, 'волейбол, теннис, футбол', 36.5865, 55.112, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24616, 'Бассейн на базе Удмуртского кадетского корпуса ПФО им.ГСС В.Г.Старикова', 'Swimming pool on the basis of V.G.Starikov Udmurt cadet military school of PFD ', true, 'В городе Воткинске, на базе Удмуртского кадетского корпуса ПФО имени Героя Советского Союза В.Г. Старикова, завершилось строительство плавательного бассейна. 25-метровый бассейн с 4 плавательными дорожками доступен не только для кадетов, но и для всех жителей Воткинска. Открытие спортивного объекта состоялось 1 сентября этого года. В церемонии приняли участие полномочный представитель Президента Российской Федерации в  Приволжском федеральном округе М.В. Бабич и Глава Удмуртской Республики А.В. Соловьев. Строительство спортивного объекта  проводилось в рамках реализации Федеральной целевой программы «Развитие физической культуры и спорта в Российской Федерации на 2006–2015 годы». Помимо бассейна при Удмуртском кадетском корпусе  будут построены плоскостные спортивные сооружения: стадион, легкоатлетические дорожки, игровые площадки и полосы препятствий. ', NULL, NULL, NULL, 'Город Воткинск', 'Удмуртская Республика', NULL, 'г. Воткинск', 'Votkinsk', 'Республика Удмуртия, г. Воткинск, ул. Серова, 14', NULL, 94710000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2014-10-01', '2015-09-01', 50000000, NULL, NULL, NULL, NULL, 'mochikchi@yandex.ru', 'бассейн', '(34145) 56677', 'http://ciur.ru/vtk/vtk_ksi/, http://www.ukk-pfo.com/', 'плавание', 53.9853, 57.0719, 167);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24623, 'Стадион «Ангара»', 'Stadium «Angara»', true, 'Завершилась реконструкция футбольного поля на стадионе «Ангара»  в городе Ангарске Иркутской области. Церемония открытия спортивного объекта состоялась 5 сентября. За время проведения строительных работ на стадионе было уложено профессиональное футбольное поле площадью 7700 квадратных метров, позволяющее проводить соревнования самого высокого уровня,  заменены ворота, зрительские трибуны и раздевалки для спортсменов. На строительство футбольного поля стадиона «Ангара» было выделено 8,421 млн рублей в рамках Подпрограммы «Развитие футбола в РФ на 2008-2015 годы».  В рамках торжественного открытия объекта на новом футбольном поле стадиона состоится матч с участием звезд иркутского и ангарского футбола прошлых лет. В нем примут участие такие именитые спортсмены, как Юрий Кузнецов, который в свое время был игроком столичного «Динамо», Александр Рычков, выступавший за московский «Локомотив» и швейцарский «Базель». ', NULL, NULL, NULL, 'Ангарский г.о.', 'Иркутская область', NULL, 'г. Ангарск', 'Angarsk', 'Иркутская область, г. Ангарск', NULL, 25703000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', NULL, '2015-09-05', 8421000, NULL, NULL, NULL, NULL, NULL, 'стадион', NULL, NULL, 'футбол', 103.8885, 52.5449, 2);
