INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24672, 'Универсальный спортивный комплекс', 'Universal sports complex', true, 'Завершилось строительство универсального спортивного комплекса в иркутском селе Еланцы Ольхонского района. Проведение церемонии открытия объекта состоялось 4 сентября 2015 года. Спорткомплекс представляет собой двухэтажное здание с двумя тренировочными помещениями: гимнастическим залом размером 42х24 м и залом индивидуальной силовой подготовки размером 12х10,9 м с линейкой современных силовых тренажеров. Помимо этого в стенах спорткомплекса размещены сауны, инвентарные помещения, медицинский кабинет, тренерские комнаты и раздевалки. Рядом со зданием спорткомплекса построен футбольный  стадион и уложены беговые дорожки.   Спортивный объект расположен в центре села, что обеспечивает ему шаговую доступность для всех жителей села. Его пропускная способность  составляет 80 человек в смену. Средства на возведение спорткомплекса выделялись из федерального бюджета. В рамках реализации Федеральной целевой программы «Развитие физической культуры и спорта в РФ на 2006-2015 годы» на строительные работы было выделено 65 млн рублей. ', NULL, NULL, NULL, 'Ольхонский район', 'Иркутская область', NULL, 'с. Еланцы', 'Yelantsy', 'Иркутская область, с. Еланцы', NULL, 25630404, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2015-09-04', 65000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'бег, силовая гимнастика, спортивная гимнастика, футбол', 106.4028, 52.8003, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24708, 'Физкультурно-оздоровительный комплекс', 'Sports complex', true, '5 сентября в селе Преображеновка Добровского района Липецкой области состоялось открытие физкультурно-оздоровительного комплекса. Новый спортивный объект был возведен на средства, выделенные из федерального бюджета в рамках реализации Федеральной целевой программы «Развитие физической культуры и спорта в РФ на 2006-2015 годы». Субсидия из федерального бюджета составила 20 млн рублей. В двухэтажном здании ФОКа разместился универсальный зал для игровых видов спорта, в котором можно проводить тренировки и соревнования по баскетболу, волейболу, бадминтону, мини-футболу, гандболу, а также раздевалки, душевые, медкабинет и административно-бытовые помещения. На втором этаже предусмотрены номера мини-гостиницы. Ранее на муниципальные и областные средства в Преображеновке возвели плавательный бассейн. Отметим, что в рамках реализации ФЦП «Развитие физической культуры и спорта в РФ на 2006-2015 годы» в области были построены крытые катки с искусственным льдом в Ельце и Задонске, а также многофункциональный спорткомплекс в Липецке, отремонтирован стадион липецкого спортивного комплекса «Сокол». В этом году в региональном центре откроется многофункциональный спортивный комплекс «Катящиеся камни». ', NULL, NULL, NULL, 'Добровский район', 'Липецкая область', NULL, 'с. Преображеновка', 'Preobrazhenovka', 'Липецкая область, с. Преображеновка', NULL, 42615456, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2015-09-05', 20000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'бадминтон, баскетбол, волейбол, гандбол, мини-футбол', 40.0757, 52.9593, 2);
