INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (24720, 'Республиканский лыжный комплекс им. Р. Сметаниной', 'R. Smetanina Republican Ski Complex ', true, 'Начало реконструкции 2-й очереди – 2006 год Завершение реконструкции 2-й очереди – 2012 год  Увеличение обеспеченности населения Республики Коми спортивными сооружениями, привлечение населения региона к регулярным занятиям физической культурой и спортом, достижение высоких результатов спортсменами Республики Коми на всероссийских и международных соревнованиях. Обеспечение возможности проведения учебно-тренировочного процесса на высоком профессиональном уровне для спортсменов, проживающих в республике и приезжающих из других регионов РФ и стран ближнего и дальнего зарубежья для сезонных тренировок, а так же проведения спортивных мероприятий регионального, всероссийского и международного уровня. Данный объект является региональным центром развития лыжных гонок и биатлона.  Всероссийские соревнования по лыжным гонкам среди молодежи 21-23 года, юниоров и юниорок 19-20 лет &#40 1995-1996 г.р.&#41  – декабрь 2014  года - Всероссийские массовые соревнования  «Лыжня России – 2015» 15 февраля 2015 года - Открытые массовые республиканские соревнования по лыжным гонкам на призы Нины и Василия Рочевых – 7 марта 2015 года -Кубок мира мастеров по лыжным гонкам среди ветеранов – 11-21 марта 2015 года - Всероссийские соревнования по лыжным гонкам среди юношей и девушек 15-16 лет &#40 1999-2000 г.р.&#41  на призы ЗМС Р.П.Сметаниной – 24-29 марта 2015 года - Открытый чемпионат РК по биатлону среди мужчин и женщин 1993 г.р. и старше - 2 этап. Открытое первенство РК среди юниоров и юниорок 1994-1995, г.р., юношей и девушек 1996-1997, 1998-1999, 2000-2001 г.г.р. - 2 этап – 30 марта – 3 апреля 2015', NULL, NULL, NULL, 'Сыктывдинский район', 'Республика Коми', NULL, 'с. Выльгорт', 'Vylgort', 'Республика Коми, с. Выльгорт, ул. Северная, 3/а', NULL, 87628405, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-03-20', '2012-10-20', 140000000, NULL, NULL, NULL, NULL, NULL, 'комплекс лыжный', '8 82130 77-075', 'http://www.cspsk.ru/', 'биатлон, лыжные гонки', 50.7133, 61.615, 168);
