INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (25177, 'Крытый каток с искусственным льдом ', NULL, true, 'Общая площадь здания составит 4043,8 м2 , полезная площадь – 3970 м2 . В здании будет располагаться ледовая арена площадью 2701,7 кв. м2 , зал общей физической подготовки – 105 м2,  хореографический класс – 89,2 м2 , кафетерий 173,4 м2 вместимостью до 33 человек. Общая вместимость зрительного зала составит 238 зрительских мест.  Цель проекта – создание условий для развития массового и профессионального спорта &#40 включая спорт высших достижений&#41 , а также приобщение населения к регулярным занятиям физической культурой и спортом.  В рамках функционирования Ледового дворца планируется реализовать следующие задачи:  - повышение интереса различных категорий граждан к занятиям физической культурой и спортом   - повышение заинтересованности руководителей предприятий &#40 организаций&#41  в проведении физкультурно-оздоровительных мероприятий для работников и членов их семей   - профилактика негативных явлений современного социума среди населения   - организация и обеспечение условий для регулярных занятий физической культурой и спортом для населения   - организация и проведение спортивно-массовых мероприятий   - повышение туристической привлекательности региона. Важность проекта для региона Крытый каток с искусственным льдом в г. Великий Устюг позволит предоставить возможность спортсменам и жителям города, а также близлежащих регионов и туристам круглогодично тренироваться и проводить соревнования, проводить полноценный активный отдых.  Социальный аспект проекта предполагает вовлечение в здоровый образ жизни и пропаганду спорта среди населения, прежде всего, среди школьников и молодежи, что положительно повлияет на социальную среду и гражданское сообщество города и области.  На крытом катке будут проходить тренировки уже действующей юношеской команды «Спартак». Потенциал для занятий хоккеем можно оценить в 7-10&#37  от числа мальчиков от 6 до 12 лет. Юношескую хоккейную секцию будут дополнительно посещать от 85 до 120 человек. Количество команд составит от 7 до 8, тренировки которых будут проходить 3-4 раза в неделю. Количество школьников и старших дошкольников, которые будут заниматься фигурным катанием, также можно оценить в 7-10&#37 . Таким образом, количество потенциальных посетителей секции фигурного катания составит от 85 до 120 человек. ', 'Крытый каток с искусственным льдом имеет размеры в осях 90х44м., размер ледового поля составляет 60х30 м.  В здании на отм. &#43 3,000 м располагаются вспомогательные помещения.  В составе крытого катка для проведения учебно-тренировочных занятий предусмотрены помещение для индивидуальной силовой подготовки, методический кабинет и хореографический класс. При зале ледовой арены запроектированы трибуны на 238 зрительских мест. Трибуны запроектированы стационарные, на основе металлического каркаса. Предусмотрено необходимое количество санитарных узлов.  Инженерное оснащение вспомогательных помещений крытого катка включает, в соответствии с нормами, устройство вентиляции, водоснабжения, канализации, отопления, электроснабжения, охранно-пожарной сигнализации, пожаротушения, средств связи.  Стальной каркас здания запроектирован по рамно-связевой схеме. Основу каркаса составляют однопролетные поперечные рамы. Жесткость рам в поперечном направлении обеспечивается шарнирным закреплением стоек рам к железобетонным фундаментам, рамным креплением ригелей рам к стойкам. Устойчивость каркаса в продольном направлении обеспечивается системой продольных вертикальных связей по стойкам рам. Общая пространственная жесткость здания обеспечивается системой горизонтальных и вертикальных связей по ригелям рам с диском, образованным стальным оцинкованным профнастилом покрытия, закрепленным к прогонам при помощи самонарезающих винтов. Для восприятия сил распора от поперечных рам на отм. -0.600 запроектированы стальные затяжки, который привариваются к пятам рам. Жесткость торцов здания образуется за счет вертикальных связей по стойкам фахверка, расположенным в плоскости торцов. На отм. 0.000 располагается ледовое поле размером 60х30м., трибуны, зал общефизической подготовки размером 9х 1 2м, вспомогательные и технические помещения, помещения для технологического проезда льдоуборочного комбайна, входная группа с тамбуром, вестибюль-фойе, гардеробные помещения, комната охраны и помещение кассы. На втором этаже комплекса &#40 отм. &#43 3.000&#41  запроектированы хореографический класс, помещения общественного питания, вспомогательные, административные помещения, методический кабинет. Принятые в проекте крытого катка архитектурно-планировочные и конструктивные решения обеспечивают: степень огнестойкости 1   класс конструктивной пожарной опасности С 0  уровень ответственности - нормальный  класс функциональной пожарной опасности Ф2.1. Применение материалов и конструкций полов, кровли, перегородок, а также отделка помещений соответствует заданию на проектирование и соответствует санитарно-гигиеническим и противопожарным требованиям в зависимости от их назначения. Здание крытого катка с искусственным льдом, в плане имеет форму прямоугольника с размерами в осях 90х44м. Отметка чистого пола 0.000. Шаг рам в продольном направлении в осях 2-14 составляет см, в осях 14-15 9м. Торцы здания &#40 по осям 1/1, 1, 15&#41  решены в виде системы фахверковых стоек, выполняющих одновременно роль несущих стоек для балок покрытия. Шаг стоек фахверка бм и 4м. Кровля здания 2-х скатная, утепленная, полистовой сборки, по стальному оцинкованному профнастилу покрытия. Уклон 15&#37 . Стеновые панели трехслойные, типа «Армакс» толщиной I50мм.  Наружная и внутренняя отделка зданий предусмотрена с применением отделочных материалов. Внутренняя отделка - улучшенная. Благоустроенность помещений - улучшенная, включающая наличие современного оснащения и инженерных систем. ', NULL, NULL, 'Великоустюгский район', 'Вологодская область', NULL, 'г. Великий Устюг', NULL, 'Вологодская область, г. Великий Устюг, ул. Железнодорожная', NULL, 19614000, NULL, 'строительство', '2014-01-12', '2015-12-20', 203000000, NULL, NULL, NULL, NULL, NULL, 'арена ледовая', NULL, NULL, 'фигурное катание, хоккей', 46.2812, 60.782, 171);
