INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (26168, 'Физкультурно – оздоровительный комплекс', NULL, true, 'Спортивная инфраструктура Нолинского района построена в 70-е годы прошлого столетия, имеющиеся спортивные залы в районе в настоящее время не могут вместить всех желающих, а также не соответствуют действующим санитарным нормам. &nbsp &nbsp &nbsp &nbsp &nbsp &nbsp &nbsp &nbsp &nbsp &nbsp &nbsp &nbsp Со строительством физкультурно-оздоровительного комплекса в г. Нолинске появится возможность более широкого вовлечения несовершеннолетних, молодежи, лиц, состоящих на учете в комиссии по делам несовершеннолетних и других категорий населения в регулярные занятия физкультурой и спортом. Регулярные занятия физкультурой и спортом существенно влияют на формирование гармоничной личности, физически и духовно здорового молодого поколения, улучшают состояние здоровья населения района. &nbsp &nbsp &nbsp &nbsp &nbsp &nbsp &nbsp &nbsp  Строительство физкультурно-оздоровительного комплекса позволит создать новые условия для развития массового спорта, совершенствования системы подготовки спортсменов высокого класса. Ведение ФОКа в эксплуатацию позволит увеличить количество проводимых спортивных и других мероприятий, как районного, так и областного уровня, а также увеличится штат тренеров и физработников в районе. Увеличится обеспеченность спортивными залами не только здорового населения, но и лиц с ограниченными возможностями, что также повлияет на удельный вес населения, систематически занимающегося физической культурой и спортом. ', 'Спортивный объект построен по типовому проекту повторного применения. Каркас основного здания из быстровозводимых легких стальных конструкций для бескаркасных зданий. Основой несущих конструкций здания являются стальные волнистые профилированные листы, применяемые в качестве и самонесущих стен и в конструкциях покрытия. Кроме того в конструкции покрытия принимаются связевые и несущие элементы из С-образных и Z-образных холодных профилей. Легкие стальные конструкции опираются на монолитный железобетонный цоколь.  Стены несущие продольные и торцевые стены выполняются из структурных секций непрерывных по высоте, с вертикальным расположением гофров. Часть здания двухэтажная решена в каркасно-монолитном варианте с балочными перекрытиями. Каркас принят из монолитного железобетона. ', NULL, NULL, 'Нолинский район', 'Кировская область', NULL, 'г. Нолинск', NULL, 'Кировская область, г. Нолинск, ул. Фрунзе, 51', NULL, 33627101, NULL, 'строительство', '2015-06-01', '2015-12-01', 49400000, NULL, NULL, NULL, NULL, 'oleg.halevin@yandex.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, волейбол, мини-футбол, настольный теннис, общая физическая подготовка, ушу, фитнес-аэробика, шахматы, шашки', 49.9227, 57.5633, 2);
