INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (28803, 'Гребной канал «Крылатское»', 'Rowing canal «Krylatskoe»', false, 'Гребной канал «Крылатское» — спортивное сооружение в Москве, в районе Крылатское. Построен в 1972—1973 годах по проекту архитекторов В. Кузьмина, Е. Горкина, Л. Зорина, В. Краснощёкова, инженеров В. Васильева, А. Кондратьева, С. Гомберга к чемпионату Европы по академической гребле 1973 года. В 1980 году на канале проходили соревнования по академической гребле и гребле на байдарках и каноэ в рамках летних Олимпийских игр.  В августе 2014 года на канале прошёл чемпионат мира по гребле на байдарках и каноэ, в рамках подготовки к которому сооружения на канале были реконструированы. По словам генерального секретаря Всероссийской федерации гребли на байдарках и каноэ Ирины Сираевой, после реконструкции Международная федерация гребли на каноэ считает канал в Крылатском лучшим в мире.', 'Гребной канал расположен в Татаровской пойме Москвы-реки, где для него было создано искусственное русло. Параллельно основному руслу расположено второе &#40 возвратное&#41 , обеспечивающее возможность проводить соревнования не только по академической гребле и гребле на байдарках и каноэ, но и кольцевые эстафеты на дистанцию до 10 км.  Длина канала — 2300 м. Ширина основного русла канала — 125 м, возвратного — 75 м, глубина 3 — 3,5 м]. В основном русле можно разместить 6 дорожек для академической гребли и 9 — для соревнований на байдарках и каноэ. В финишной зоне — зрительские трибуны на 3400 мест под крышей-козырьком.', 'Rowing canal &quot Krylatskoe&quot  - sports facility in Moscow, in the Krylatskoye district. Built in 1972-1973 under the project of architects V. Kuzmin, E. Gorkina, Zorin, V. Krasnoschёkova, engineers Vasilyeva, A. Kondratyev, S. Gomberg to to 1973 Rowing European Championship year. In 1980, the channel passed competitions in rowing and rowing and canoeing within the Summer Olympic Games.  In August 2014 the channel has passed the world championship in rowing and canoeing, in preparation for which the construction of the channel were reconstructed. According to the General Secretary of the All-Russian Federation of rowing and canoeing Siraeva Irina, after the reconstruction of the International Federation of canoeing said channel in Krylatskoye best in the world.', 'Rowing canal situated in Tatarovskoy floodplain of the Moscow River, where an artificial channel was created for him. Parallel to the main channel is a second (return), it is possible to carry out the competition not only in rowing and rowing and canoeing, but also the relay ring at a distance of 10 km.  The channel length -. 2300 m width of the main channel of the channel - 125 m, the return - 75 m, the depth of 3 - 3.5 m]. The mainstream can accommodate 6 lanes for rowing and 9 - for competitions and canoeing. In the finish area - spectator stands for 3400 seats under the roof-peak.', 'Западный округ', 'Москва', NULL, 'г. Москва', 'Moscow city', 'г. Москва, Крылатская ул., д. 2', NULL, 45319000, NULL, 'реконструкция', NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, 'канал гребной', NULL, NULL, 'гребля на байдарках и каноэ, гребной слалом', 37.4352, 55.7546, 2);
